syntax = "proto3";
package SG;

message MsgBody  {

    GameStartNotify   game_start_notify = 1;   //游戏开始

    NotifyBank        notify_bank  = 2;   //通知开始抢庄
    GrabBankReq       grab_bank_req = 3;   // 
    GrabBankRes       grab_bank_res = 4; 
    ComfirmBankNotify    comfirm_bank_notify = 5; //确定庄家返回

    BetReq            bet_req = 6;
    BetRes            bet_res = 7;

    StartBetNotify    start_bet_notify = 8;

    DealCardNotify    deal_card_notify = 9;

    CardResultReq     card_result_req = 10;

    CardResultRes     card_result_res = 11;

    GameConcludeNotify  game_conclude_notify = 12;

    GameReconnectInfo game_reconnect_info = 13;
}

message RoomInfo  {
    string room_base_note  = 1 ;      //房间底注
    string room_code = 2;             //房间编号信息
}

message PlayerInfo  {
    uint32 player_id = 1;
    uint32 seat_id = 2;         //座位ID
    string username = 3;
    float  gold = 4;
    uint32 headimg_id = 5;
    uint32 sex = 6;      //性别

    sint32 grab_status = 7; //存在该字段表示已经抢庄,没抢庄= -1
    sint32 bet_multiple = 8; //存在该字段表示已经下注,没下注= -1
    sint32 open_carded = 9 ; //存在该字段表示已经摊牌,没摊牌 = -1
    CardResult card_result = 10; //如果该玩家已经亮牌,里面有数据,没摊牌 = nil
}

message GameStartNotify  {      //游戏开始通知
    repeated PlayerInfo player_list = 1;           //所有玩家
    RoomInfo room_info = 2;                        //房间信息
}

message NotifyBank {      //通知开始抢庄
    uint32 time_out = 1;                                     //抢庄倒计时(单位秒)
}

message GrabBankReq  {         //抢庄请求
    uint32 player_id = 1;
    uint32 grab_status = 2;      //是否抢庄
}

message GrabBankRes  {        
    uint32 seat_id = 1;
    uint32 grab_status = 2;      //是否抢庄
}

message ComfirmBankNotify {      //确定庄家返回
    uint32 bank_seat_id = 1;                                 //最终庄家的id
    repeated uint32 higher_grab_multiple_arr = 2;            //抢到了最高倍数的id数组 or 竞争庄家数组 可改
}

message StartBetNotify {      //通知开始下注
    uint32 bank_seat_id = 1;
    repeated uint32 bet_multiple_arr = 2;                    //该玩家能下倍数的数组
    uint32 time_out = 3;                                     //下注的倒计时(单位秒)
}

message BetReq  {
    uint32 player_id = 1;
    uint32 bet_multiple = 2;           //下注倍数
}

message BetRes  {
    uint32 seat_id = 1;
    uint32 bet_multiple = 2;           //下注倍数    
}

message DealCardNotify {               //下注完成 发牌
    repeated uint32 card_arr = 1;
    uint32 val_type = 2;               //玩家的牌型, 0~9 零点到九点 10 三公 11 大三公 12 至尊
    uint32 time_out = 3;               //摊牌的倒计时(单位秒)
}

message CardResult  {
    uint32 seat_id = 1;
    repeated uint32 card_arr = 2;       //每个玩家的3张牌
    uint32 val_type = 3;                //玩家的牌型, 0~9 零点到九点 10 三公 11 大三公 12 至尊
}

message CardResultReq  {        //开牌请求
    uint32 player_id = 1;
} 

message CardResultRes  {        //开牌结果
    CardResult card_result = 1;
}

message GameConclude {
    uint32 seat_id = 1;         //玩家座位号
    float gold = 2;             //玩家结算后的金币数量
    float score = 3;            //输赢的分数
}

message GameConcludeNotify  {        //结算推送
    repeated GameConclude game_conclude = 1;
    uint32 bank_seat_id = 2;    //庄家位置
}

//重连信息
message GameReconnectInfo  {
    uint32 game_state  =1;                             //游戏状态 1=开始(服务器有1秒的等待时间，播放开始动画)；2=抢庄阶段；3=下注阶段；4=开牌阶段；5=结算（结算有1.5秒的时间延时）
    uint32 time_out = 2;                               // 当前阶段剩下的时间(单位秒)
    uint32 bank_seat_id = 3;                           //庄家id
    repeated uint32 bet_multiple_arr = 4;              //该玩家能下倍数的数组
    GameStartNotify game_start_notify = 5;              //游戏开始的信息，包括所有玩家的信息
}
