syntax = "proto3";
package EBG;



message MsgBody {
    GameStartNotify         game_start_notify       = 1;    //游戏初始化消息

    //游戏环节消息
    GameBankStartNotify     game_bank_start_notify  = 2;    //开始抢庄环节通知
    GameBankReq             game_bank_req           = 3;    //抢庄请求
    GameBankRes             game_bank_res           = 4;    //抢庄回应

    GameConfirmBankNotify   game_confirm_bank_notify= 5;    //确定庄家回应

    GameBetStartNotify      game_bet_start_notify   = 6;    //开始下注环节通知
    GameBetReq              game_bet_req            = 7;    //下注请求
    GameBetRes              game_bet_res            = 8;    //下注回应

    GameDiceNotify          game_dice_notify        = 9;    //摇骰子通知

    GameSendCardNotify      game_send_card_notify   = 10;   //发牌通知

    GameCardResultNotify    game_card_result_notify = 11;   //开牌通知

    GameCardResultReq       game_card_result_req    = 12;   //开牌请求
    GameCardResultRes       game_card_result_res    = 13;   //开牌结果

    GameConcludeNotify      game_conclude_notify    = 14;   //结算通知
    //游戏内部其他信息
    GameErrRes              game_err_res            = 15;   //游戏内部错误信息
    //游戏内部其他信息
    GameReconnectInfo       game_reconnect_info     = 16;
}


message PlayerInfo  {
    uint32 player_id = 1;
    uint32 seat_id = 2;                         //座位ID
    string username = 3;
    float  gold = 4;
    uint32 headimg_id = 5;
    uint32 sex = 6;                             //性别

    sint32 bank_value = 7;                      //抢庄倍数 未抢庄为-1
    sint32 bet_value = 8;                       //下注倍数 未下注未-1
    CardResult card_result = 9;                 //牌结果
}

message RoomInfo  {
    string room_base_note  = 1;                 //房间底注
    string room_code = 2;                       //房间编号信息
    uint32 round = 3;                           //当前局数
}

//通知开始游戏
message GameStartNotify {
    repeated PlayerInfo player_list = 1;        //所有玩家
    RoomInfo room_info = 2;                     //房间信息 
}

//通知开始抢庄
message GameBankStartNotify {
    repeated uint32 bank_info = 1;              //可抢庄所有倍数信息 
    uint32 time_out = 2;                        //超时时间
}

message GameBankReq {   //抢庄请求
    uint32 bank_value = 1;                      //抢庄倍数
}

message GameBankRes {   //抢庄回应
    uint32 seat_id = 1;                         //抢庄的座位号
    uint32 bank_value = 2;                      //抢庄倍数
}

//确定庄家通知
message GameConfirmBankNotify {    
    uint32 banker_seat_id = 1;                  //庄家座位号
    repeated uint32 banker_compete_seat_id = 2; //竞争抢庄玩家的座位号
    uint32 bank_value = 3;                      //庄家抢庄倍数
}

//通知开始下注
message GameBetStartNotify {
    uint32 banker_seat_id = 1;                  //庄家座位号
    repeated uint32 bet_info = 2;               //可下注所有值的信息
    uint32 time_out = 3;                        //超时时间

}

message GameBetReq {    //下注请求
    uint32 bet_value = 1;                       //下注倍数
}

message GameBetRes {    //下注回应
    uint32 seat_id = 1;                         //下注的座位号
    uint32 bet_value = 2;                       //下注倍数
}

//摇骰子通知
message GameDiceNotify {
    repeated uint32 dice_val = 1;               //骰子点数
}

//发牌通知
message GameSendCardNotify {   
    uint32 seat_id = 1;                         //先发牌人的座位号
    uint32 time_out = 2;                        //超时时间
}

//开牌通知
message GameCardResultNotify {
    uint32 time_out = 1;                        //超时时间
}

//开牌请求
message GameCardResultReq {
    uint32 player_id = 1;
}

message CardResult {
    uint32 seat_id = 1;
    repeated uint32 card_arr = 2;               //每个玩家的2张牌
    float card_value = 3;                       //点数牌则为其值 二八杠10 1宝~9宝 11~19 天王20
}

//开牌结果
message GameCardResultRes {
    CardResult card_result = 1;
}

message CardResultInfo {
    uint32 seat_id = 1;                         //座位号
    float gold = 2;                             //结算后的金币数
    float score = 3;                            //本局的输赢情况
    bool is_user_conclude = 4;                  //该玩家是否继续游戏（若金币不足则为false 表示该玩家不能继续游戏 其他玩家不受影响）
}

//结算通知
message GameConcludeNotify { 
    repeated CardResultInfo result_info = 1;    //牌值
    uint32 banker_seat_id = 2;                  //庄家座位号
    bool is_continue_game = 3;                  //是否继续游戏
}

message GameErrRes {    //游戏内部错误信息
    uint32 err_id = 1;                          //1钱不足 2状态错误 3无效玩家id 4无效抢庄值 5无效下注值
}

//重连信息
message GameReconnectInfo  {
    uint32 game_state = 1;                              //游戏状态 1游戏开始 2抢庄 3定庄 4下注 5摇骰子 6发牌 7开牌 8结算
    sint32 banker_seat_id = 2;                          //庄家id 暂时无庄家则为-1
    repeated uint32 bank_info = 3;                      //可抢庄所有倍数信息
    repeated uint32 bet_info = 4;                       //可下注所有值的信息
    GameStartNotify game_start_notify = 5;              //游戏开始的信息 包括所有玩家的信息
    repeated uint32 dice_val = 6;                       //骰子点数 无骰子点数则为两个-1
    repeated uint32 already_out_card = 7;               //已出牌值
    sint32 bank_value = 8;                              //庄家抢庄倍数 没有则为-1
    uint32 time_out = 9;                                //当前阶段剩下的时间(单位秒)
}
