syntax = "proto3";
package BRNN;

message MsgBody  {
    GameStartNotify   game_start_notify = 1;   //游戏开始

    SendCardNotify    send_card_notify = 2;   //发牌 

    StartBetNotify    start_bet_notify = 3;    //开始下注
    BetReq            bet_req = 4;
    BetNotify         bet_notify = 5;
    OtherBetNotify    other_bet_notify = 6;

    ShowCardNotify    show_card_notify = 7;     //开牌通知

    GameConcludeNotify  game_conclude_notify = 8; //结算

    GameReconnectInfo game_reconnect_info = 9; //加入房间

    OtherUsersReq     other_users_req = 10;
    OtherUsersRes     other_users_res = 11;

    WaitNextNotify    wait_next_notify = 12;    //等待下一局
}

message PlayerInfo  {
    uint32 player_id = 1;
    uint32 seat_id = 2;         //座位ID
    string username = 3;
    string gold = 4;
    uint32 headimg_id = 5;
    uint32 sex = 6;      //性别
}

//房间信息
message RoomInfo  {
    string room_code = 1;             //房间编号信息
}

//游戏开始通知 洗牌
message GameStartNotify  {            
    RoomInfo room_info = 1;           //房间信息
    uint32 time_out = 2;              //洗牌时间
}

//发牌通知
message SendCardNotify {
    uint32 time_out = 1;                // 发牌时间
}

//通知开始下注
message StartBetNotify {      
    repeated uint32 bet_value_arr = 1;      //该玩家能下倍数的数组
    uint32 time_out = 2;                    //下注的倒计时(单位秒)
}

//下注请求
message BetReq   {
    uint32 player_id = 1;
    uint32 bet_value = 2;         //下注值
    uint32 loc = 3;			      //下注的位置( 1=天，2=地，3=玄 4=黄)
}

//下注筹码值情况
message BetValueInfo {
    uint32 loc = 1;             //每个筹码的位置( 1=天,2=地,3=玄,4=黄)
    repeated uint32 bet_value = 2;       //下注的筹码值
}

//已下注筹码数量信息
message BetNumInfo {
    uint32 user_bet_num = 1;        //我已下注数量
    uint32 sum_bet_num = 2;         //所有玩家已下注数量
}

//下注通知  此通知只会发给自己
message BetNotify  {
    uint32 result = 1 ;                         // 下注结果: 1=成功,2=下注超过本人金币的1/3(即金币不足),3=达到限红
    string  gold = 2;                            //更新玩家金币
    BetValueInfo bet_value_info = 3;            // 下注信息
    repeated BetNumInfo bet_num_info = 4;       // 所有玩家在各个位置下注总量(天地玄黄)  
}

//其他玩家下注通知 每隔一段时间一起发过来
message OtherBetNotify {
   repeated BetValueInfo bet_value_info = 1;    // 筹码应该丢在那个位置
   repeated BetNumInfo bet_num_info = 2;        // 所有玩家在各个位置下注总量(天地玄黄)  
}

message CardMsgInfo {
    uint32 loc = 1 ;                     //1=天、2=地，3=玄，4=黄，5=庄
    repeated uint32 card_arr = 2 ;       //每个位置5张牌
    uint32 val_type = 3;                 //对应位置的牌型 0=无牛、1=牛1 ... 10=牛牛、11=四花牛、12=五花牛、13=炸弹牛、14=五小牛
    uint32 val_betValue = 4;             //牌对应的倍数
}

//开牌通知
message ShowCardNotify {                    
    repeated CardMsgInfo card_msg_info = 1; 
    uint32 time_out = 3;                    //开牌的倒计时(单位秒)
}

//天地玄黄输赢情况
message EndterResult {
    repeated uint32 result = 1;               //1表示赢，0表示输。如 1010 表示: 天赢、地输、玄赢、黄输
}

//结算通知
message GameConcludeNotify  { 
    EndterResult enter_result = 1;              //天地玄黄输赢情况
    string gold = 2;                            //玩家结算后的金币数量
    string score = 3;                           //输赢的分数 
    uint32 time_out = 4;                        //派奖时间
    bool is_bet_req = 5;                        //是否有下注，true = 有下注,false = 没下注
}

//休息通知
message WaitNextNotify {
    uint32 time_out = 1;
}

//重连信息
message GameReconnectInfo  {
    RoomInfo room_info = 1;                        //房间信息
    uint32 game_state  = 2;                        //游戏状态 1=开始(服务器有2秒的等待时间，播放搓牌动画)；2=发牌阶段；3=下注阶段；4=开牌；5=结算（结算有2秒的时间延时）
    uint32 time_out    = 3;                        //当前阶段剩下的时间(单位秒)
    repeated uint32 bet_value_arr = 4;             //该玩家能下倍数的数组
    repeated BetNumInfo bet_num_info = 5;          //天地玄黄已下注数量
    repeated BetValueInfo bet_value_info = 6;      //桌面筹码值 只保存最后 15 个下注筹码值
    repeated CardMsgInfo card_msg_info = 7;        //如果已经到了看牌阶段，里面有牌数据
    repeated EndterResult enter_result = 8;        //玩家进入游戏输赢记录推送
    string gold = 9;                               //玩家剩余金币
}

//其它玩家列表
message OtherUsersReq {
    uint32 player_id = 1;
}

message OtherUsersRes {
    repeated PlayerInfo player_list = 1;      //20名的玩家信息
    uint32 totalplayers = 2;                  //房间总玩家数量
}
