syntax = "proto3";
package Bank;



message MsgBody  {

    ErrorRes            err_res = 100;

    AccountToSafeBoxReq    account_to_safebox_req = 1;            //存款即把金币存入到保险箱中
    AccountToSafeBoxRes    account_to_safebox_res = 2;  

    SafeBoxToAccountReq    safebox_to_account_req = 3;            //取款即从保险箱中到账户余额
    SafeBoxToAccountRes    safebox_to_account_res = 4;  

    TransferToOtherReq     transfer_to_other_req = 5;             //转账
    TransferToOtherRes     transfer_to_other_res = 6;       

    WithdrawToBankCardReq  withdraw_to_bankcard_req = 7;          //提现到银行卡
    WithdrawToBankCardRes  withdraw_to_bankcard_res = 8;

    ResearchBankInfoReq    research_bank_info_req = 9;            //查询银行信息
    ResearchBankInfoRes    research_bank_info_res = 10;  

    BindBankCardReq        bind_bankcard_req = 11;                //绑定到银行卡
    BindBankCardRes        bind_bankcard_res = 12;

    GetBoundBankCardReq    get_bound_bankcard_req = 13;            //查找绑定的银行卡
    GetBoundBankCardRes    get_bound_bankcard_res = 14;   

    ChangeBoundBankCardReq change_bound_bankcard_req = 15;        //改变绑定的银行卡
    ChangeBoundBankCardRes change_bound_bankcard_res = 16;    

    GetAccountDetailsReq get_account_details_req = 17;   //账户明细
    GetAccountDetailsRes get_account_details_res = 18;

    GetWithdrawalsRecordReq get_withdrawals_recordReq = 19;   //提现记录
    GetWithdrawalsRecordRes get_withdrawals_recordRes = 20;

    GetRechargeRecordReq get_recharge_record_req = 21;   //充值记录
    GetRechargeRecordRes get_recharge_record_res = 22;

    RechargePlatformReq   recharge_platform_req = 23;    //充值渠道请求
    RechargePlatformRes   recharge_platform_res = 24;    //充值渠道回应

    RechargeUrlReq      recharge_url_req  = 25;          //充值网页请求
    RechargeUrlRes      recharge_url_res  = 26;          //充值网页回应

    CheckRechargeReq    check_recharge_req = 27;         //充值审核
    CheckRechargeRes    check_recharge_res = 28;         //充值审核
}



message ErrorRes {
    int32 err_no = 1;           //错误原因
    int32 err_subno = 2;        //错误子原因
    string err_msg = 3;         //错误描述
}


message AccountToSafeBoxReq  {
    uint32 userid = 1;
    string money = 2 ;       //要存的钱
}

message AccountToSafeBoxRes  {
    bool ok = 1;
    string account_balance = 2;     
    string safe_balance = 3;
}


message SafeBoxToAccountReq  {
    uint32 userid = 1;
    string money = 2 ;       //要取的钱
}

message SafeBoxToAccountRes  {
    bool ok = 1;
    string account_balance = 2;     
    string safe_balance = 3;
}



message TransferToOtherReq  {
    uint32 userid = 1;
    uint32 recv_userid = 2;          //接收者的userid
    string money = 3;                //转账的钱
    string safe_code = 4;            //安全码
}

message TransferToOtherRes  {
    bool ok = 1;                // true = 成功 false = 失败
    string msg = 2;             //提示信息
}

message WithdrawToBankCardReq  {
    uint32 userid = 1;
    uint32 index = 2;                //银行卡的index
    string money = 3;                //提现的钱
    string safe_code = 4;            //安全码
    string channel = 5;              //渠道id
}


message WithdrawToBankCardRes  {
    uint32 status = 1;              //1=申请成功 2=输入金额有误 3=申请失败(不明原因失败)
    string account_balance = 2;     
    string safe_balance = 3;
    string withdraw_msg = 4;        //提示消息
}


message ResearchBankInfoReq {      //查询银行信息
    uint32 userid = 1;
    string channel = 2;            //渠道
}

message ResearchBankInfoRes {
    string account_balance = 1;     
    string safe_balance = 2;
    string withdraw_rate = 3;            //提现率
    string transfer_min_gold = 4;
    string transfer_max_gold = 5;
    string transfer_frozen_gold = 6;
    string cash_min_gold = 7;
    string cash_max_gold = 8;
    string cash_frozen_gold = 9;
    string transferable_balance = 10;     //可转余额
    string withdrawable_balance = 11;     //可取余额
    string transfer_rate = 12;            //转账手续率
    uint32 close_transform = 13;           //关闭转账 0开 1关
    uint32 close_withdraw = 14;            //关闭提现 0开 1关
}


message BindBankCardReq  {              //绑定银行卡
    uint32 userid = 1;
    string name = 2;                    //账户姓名
    string cardid = 3;                  //银行卡号
    string card_name = 4;               //银行卡名称
    string safe_code = 5;               //安全码
}

message BindBankCardRes  {
    bool ok = 1;
}

message GetBoundBankCardReq{
    uint32 userid = 1;
    string channel = 2;    
}

message BankCardItem{
    uint32 index = 1;
    string name = 2;             //账户姓名
    string cardid = 3;           //银行卡号
    string card_name = 4;        //银行名称     
}

message GetBoundBankCardRes{
    repeated  BankCardItem bankCardItem = 1;
}


message ChangeBoundBankCardReq{
    uint32 userid = 1;
    uint32 index = 2;
    string name = 3;                    //账户姓名
    string cardid = 4;                  //银行卡号
    string card_name = 5;               //银行卡名称
    string safe_code = 6;               //安全码
}

message ChangeBoundBankCardRes{
    bool ok = 1;
}


message GetAccountDetailsReq {
    uint32 userid = 1;
    uint32 typeIndex = 2;
    uint32 timeIndex = 3;
    uint32 page_idx = 4;   //起始页
    uint32 page_num = 5;   //每页条数
}

message AccountDetailList{
    string detailTypeStr = 1; //明细类型
    string budgetGold = 2; //收支金额
    uint32 time = 3; //时间
}

message GetAccountDetailsRes {
    repeated AccountDetailList accountDetailList = 1;
}


message GetWithdrawalsRecordReq {
    uint32 userid = 1;
    uint32 timeIndex = 2;
    uint32 page_idx = 3;   //起始页
    uint32 page_num = 4;   //每页条数
}
message WithdrawalsRecordList{
    string withdrawalsStatusStr = 1; //提现状态
    string withdrawalsGold = 2; //提现金额
    uint32 time = 3; //时间
}

message GetWithdrawalsRecordRes {
    repeated WithdrawalsRecordList withdrawalsRecordList = 1;
}

message GetRechargeRecordReq {
    uint32 userid = 1;
    uint32 timeIndex = 2;
    uint32 page_idx = 3;   //起始页
    uint32 page_num = 4;   //每页条数
}

message  RechargeRecordList{
    string rechargeStatusStr = 1; //充值状态
    string rechargeGold = 2; //充值金额
    uint32 time = 3; //时间
    string rechargeModeStr = 4; //充值方式
}

message GetRechargeRecordRes {
    repeated RechargeRecordList rechargeRecordList = 1;
}

//充值平台列表请求
message RechargePlatformReq {
    uint32 userid = 1;
    string channel = 2;            //渠道
}

message VipCustomListInfo {
    string name = 1;                //客服名称
    string info = 2;                //客服信息
    string link = 3;                //链接
}

message CustomCheckInfo {
    string bank_name = 1;                //银行名称
    string card_num = 2;                 //银行卡号
    string holder_name = 3;              //持卡人姓名
    string opr_time = 4;                 //入款时间
}


message PayInfo  {
    uint32 pay_platform_type = 1;       //充值平台1=鑫宝支付  2=人工充值 3=审核充值
    uint32 pay_id = 2;                  //支付id 1=人工充值、2=审核充值 、3=支付宝、4=微信支付、5=QQ钱包、6=京东钱包、7=银联云闪付、8=银联快捷
    string max_amount = 3;              //最大金额
    string min_amount = 4;              //最小金额
    repeated string quick_num_list = 5; //快速充值金额
    repeated VipCustomListInfo vip_custom_list_info = 6;
    CustomCheckInfo custom_check_info = 7;
    string pay_sub_name = 8;             //后台填的支付名称
    uint32 data_index = 9;              //唯一标识
    string extral = 10; //二维码数据
}

message RechargePlatformInfo {
    uint32 pay_label_id = 1;
    string pay_label_name = 2;                //充值平台名支付宝微信等,对上面id的解释
    repeated PayInfo pay_info = 3;
    string des_info = 4;                  //折扣描述
}

//充值平台列表回应
message RechargePlatformRes {
    repeated RechargePlatformInfo platform_Info = 1;
    //repeated VipRechargePlatformInfo vip_platform_Info = 2;
}

//充值网页请求
message RechargeUrlReq {
    uint32 userid = 1;              //标签id 1=支付宝、2=微信支付、3=QQ钱包、4=京东钱包、5=银联云闪付、6=银联快捷 7公司入账 8 代理充值
    uint32 pay_platform_type = 2;   //充值平台
    uint32 pay_id = 3;              //充值方式id
    string recharge_amount = 4;     //充值金额
    uint32 data_index = 5;        //唯一标识
    uint32 pay_label_id = 6;      //标签id
}

//充值网页回应
message RechargeUrlRes {
    bool is_ok = 1;                 //是否获取成功
    string recharge_url = 2;        //充值网页
    string err_msg = 3;             //错误信息
}

//充值审核
message CheckRechargeReq {
    uint32 userid = 1;
    string name = 2;    //充值姓名
    string time = 3;    //入款时间
    string gold = 4;    //入款金额
    uint32 data_index = 5;        //唯一标识
    uint32 pay_label_id = 6;      //标签id
}

message CheckRechargeRes {
    uint32 status = 1; //0失败 1=成功
}
