syntax = "proto3";
package PbLobby;



message MsgBody  {
    ErrorRes            err_res = 1;

    HeartBeatReq        heart_beat_req = 2;
    HeartBeatRes        heart_beat_res = 3;
    
    GetVipLevelInfoReq  get_viplevelinfo_req = 4;
    GetVipLevelInfoRes  get_viplevelinfo_res = 5;

    GetUserMailReq      get_usermail_req = 12;
    GetUserMailRes      get_usermail_res = 13;

    GetUserMailDetailReq      get_usermaildetail_req = 14;
    GetUserMailDetailRes      get_usermaildetail_res = 15;

    GetGameAllListReq   get_gamealllist_req = 16;       //获取所有游戏列表
    GetGameAllListRes   get_gamealllist_res = 17;      

    GetGoldRankListReq  get_goldranklist_req = 18;     //获取金币排行
    GetGoldRankListRes  get_goldranklist_res = 19;

    EnterRoomReq        enter_room_req = 20;
    EnterRoomRes        enter_room_res = 21;

    AskJoinedGameInfoReq ask_joined_gameinfo_req = 22;
    AskJoinedGameInfoRes ask_joined_gameinfo_res = 23;

    GetGameRoomListReq  get_gameroom_list_req = 24;     //获取游戏房间列表
    GetGameRoomListRes  get_gameroom_list_res = 25;

    ReadMailAllReq         read_mail_all_req = 26;
    DeleteReadMailReq      delete_read_mail_req = 27;

    ActionReq            action_req  = 28; //活动请求
    ActionRes            action_res  = 29; //活动返回
    
    TheActionReq        the_action_req  = 30; 
    TheActionRes        the_action_res  = 31;

    GetVipRewardReq       get_vip_reward_req = 32;
    GetVipRewardRes       get_vip_reward_res = 33;

    GetAchieveRewardReq    get_achieve_reward_req = 34;
    GetAchieveRewardRes    get_achieve_reward_res = 35;

    GetActionRewardReq      get_action_reward_req = 36;
    GetActionRewardRes      get_action_reward_res = 37;

    GetGameRecordListReq      get_gamerecord_list_req = 41;
    GetGameRecordListRes      get_gamerecord_list_res = 42;

    GetGameRecordDetailReq    get_gamerecord_detail_req = 43;
    GetGameRecordDetailRes    get_gamerecord_detail_res = 44;

    
    PromoteMsgReq   promote_msg_req = 45;
    PromoteMsgRes   promote_msg_res = 46;

    PromoteCourseReq promote_course_req = 47;
    PromoteCourseRes promote_course_res = 48;

    PromoteDetailReq promote_detail_req = 49;
    PromoteDetailRes promote_detail_res = 50;

    GetFriendGameInofReq      get_friendgame_info_req = 51;           //获取游戏和房间的信息
    GetFriendGameInofRes      get_friendgame_info_res = 52;

    FriendGameCreateReq       friendgame_create_req = 53;             //创建好友约战
    FriendGameCreateRes       friendgame_create_res = 54;

    FriendGameJoinReq       friendgame_join_req = 55;                 //加入好友约战
    FriendGameJoinRes       friendgame_join_res = 56;

    FriendGamePrePareJoinReq    friendgame_prepare_join_req = 57; //预加入好友约战
    FriendGamePrePareJoinRes    friendgame_prepare_join_res = 58; //预加入好友约战

    GetAllAttainmentReq  get_all_attainment_req = 61;                 //获取所有的成就
    GetAllAttainmentRes  get_all_attainment_res = 62;        

    UseAttainmentReq    use_attainment_req = 63;                      //使用某个成就
    UseAttainmentRes    use_attainment_res = 64;

    UnUseAttainmentReq    unuse_attainment_req = 65;                  //卸下某个成就
    UnUseAttainmentRes    unuse_attainment_res = 66;


    AdvertisementReq   advertisement_req = 98;                      //广告请求
    AdvertisementRes   advertisement_res = 99 ;                     //广告返回

    MarqueeInfoNotify       marquee_info_notify = 100;   //跑马灯

    BindParentIdReq  bind_parentid_req = 110;                      //绑定上级玩家id
    BindParentIdRes  bind_parentid_res = 111;                      //绑定上级玩家id

    BindWindowReq    bind_window_req = 112;                        //绑定上级id窗口
    BindWindowRes    bind_window_res = 113;                        //绑定上级id窗口

    NotReadNoticeNotify  not_read_notice_notify = 114;             //存在未读公告或者邮件

    GetPromoteRewardReq get_promote_reward_req = 121;              //领取推广奖励
    GetPromoteRewardRes get_promote_reward_res = 122;              //领取推广奖励

    KYGameUrlReq ky_game_url_req = 130;                         //开元棋牌网页请求
    KYGameUrlRes ky_game_url_res = 131;                         //开元棋牌网页回应

    LeaveKYGameHallPush leave_kygamehall_push = 132;           //离开开元大厅通告

    GetMFWZGameInofReq      get_mfwzgame_info_req = 140;           //获取魔法王者的怪物配置信息
    GetMFWZGameInofRes      get_mfwzgame_info_res = 141;

    GetRedbagRainActReq get_redbag_rain_act_req = 151; //获取红包雨活动信息请求
    GetRedbagRainActRes get_redbag_rain_act_res = 152;

    GetMoneyTreeActReq get_money_tree_act_req = 153; //获取摇钱树活动信息请求
    GetMoneyTreeActRes get_money_tree_act_res = 154;

    HitRedbagRainReq hit_redbag_rain_req = 155; //抢红包请求
    HitRedbagRainRes hit_redbag_rain_res = 156;

    HitMoneyTreeReq hit_money_tree_req = 157; //摇钱树请求
    HitMoneyTreeRes hit_money_tree_res = 158;

    GetLobbyRankListShowReq  get_lobby_rank_list_show_req = 159;    //获取大厅显示的是哪个榜单的前三个玩家信息
    GetLobbyRankListShowRes  get_lobby_rank_list_show_res = 160;    //获取大厅显示的是哪个榜单的前三个玩家信息

    ExchangeCodeReq exchange_code_req = 161; //兑换码请求
    ExchangeCodeRes exchange_code_res = 162; //兑换码回应

    GetBindRewardReq get_bind_reward_req = 163; //领取绑定奖励请求
    GetBindRewardRes get_bind_reward_res = 164; //领取绑定奖励回应

    GetRewardNotify get_reward_notify = 165; //客户端领取后通知一下复位

    GoldDetialReq gold_detial_req = 166; //请求金流信息
    GoldDetialRes gold_detial_res = 167; //返回金流信息

    GetVipLevelRewardReq get_vip_level_reward_req = 168; //领取vip等级奖励请求
    GetVipLevelRewardRes get_vip_level_reward_res = 169; //领取vip等级奖励返回

    GetVipListInfoReq get_vip_list_info_req = 170; //vip详细数据请求（不带状态）
    GetVipListInfoRes get_vip_list_info_res = 171; //vip详细数据返回 (不带状态)、
}

message ErrorRes {
    uint32 err_no = 1;           //错误原因
    uint32 err_subno = 2;        //错误子原因
    string err_msg = 3;         //错误描述
}

message HeartBeatReq  {
    uint32 userid = 1;
    uint32 index = 2;           //心跳次数，超时重置0
    uint32 now = 3;
}

message HeartBeatRes  {
    uint32 index = 1;           //返回的次数+1
    uint32 now = 2;
}

message VipRewardInfo {
    string type = 1;  //类型(后台值) upgrade 晋级奖励 week 周奖励 month 月奖励
    string reward = 2; //奖励值
    uint32 status = 3; //0未达标 1可领 2已领
    uint32 reward_type = 4; //1积分 2金币
}

message VipLevelInfo {
    uint32 vip_level = 1; //vip等级
    string vip_des = 2; //vip信息title
    uint32 need_score = 3; //vip需要积分
    string desc = 4; //描述
    uint32 accelerate_switch = 5; //存款加速 0无 1有
    uint32 customer_switch = 6; //专属客服 0无 1有
    repeated VipRewardInfo reward_info = 7; //奖励信息
}
message GetVipLevelInfoReq  {
    uint32 userid = 1;
    uint32 recharge_nums = 2; //充值金额
    uint32 user_vip_info = 3; //玩家VIP等级
    uint32 cur_vip_level = 4; //当前选定的vip等级
}

message GetVipLevelInfoRes  {
    repeated VipLevelInfo vip_info = 1; //每个vip信息
    string user_score = 2; //玩家目前vip积分
}


message GetUserMailReq  {
    uint32 userid = 1;
    uint32 mail_type = 2;    //0=邮件 1=公告
    string channel = 3;  //渠道
}

message MailLists  {
    uint32 mail_id = 1;
    uint32 receiver_id = 2;
    uint32 sender_id = 3;
    string send_time = 4;       //单位毫秒
    string expire_time = 5;
    string title = 6;
    uint32 is_read = 7;         //是否已读 0未读，1已读
}

message GetUserMailRes  {
    repeated MailLists mail_lists = 1;
    uint32 mail_type = 2;    //0=邮件 1=公告
}

message GetUserMailDetailReq  {
    uint32 userid = 1;
    uint32 mail_id = 2;      //读取的邮件ID
    uint32 mail_type = 3;    //0=邮件 1=公告
}

message GetUserMailDetailRes  {
    uint32 mail_id = 1;
    string title = 2;
    string content = 3;
    string send_time = 4;
}

message ReadMailAllReq {      //一键读取所有邮件  返回GetUserMailRes mail_type为0
    uint32 userid = 1;
}

message DeleteReadMailReq {     //删除所有已读邮件 返回GetUserMailRes mail_type为0
    uint32 userid = 1;
}

message GetGameAllListReq  {
    uint32 userid = 1;
}

message GameRoomInfo  {
    uint32 room_level = 1;
    string room_base_note = 2;          //底注
    string room_min_entrance = 3;       //准入
    string room_info = 4;
    bool needChooseCarry = 5; //是否需要设置携带
    uint32 min_player = 6;       //支持最小人数
    uint32 max_player = 7;     
    repeated string bet_limit = 8;      //限红    
}

message GameInfo  {
    uint32 game_type = 1;
    string game_name = 2;
    bool is_open = 3;
    uint32 game_tab = 4;            //游戏分类标签
    string extral = 5;              //额外的定义json格式
}

message GetGameAllListRes  {
    uint32 userid = 1;
    repeated GameInfo game_lists = 2;
   
}

message GetGameRoomListReq  {
    uint32 userid = 1;
    uint32 game_type = 2;
}

message GetGameRoomListRes  {
    GameInfo game_info = 1;
    repeated GameRoomInfo game_room_lists = 2;          //该类游戏房间列表
}

message GetGoldRankListReq  {
    uint32 userid = 1;
    uint32 type = 2;              //1=土豪榜 2=连赢榜 3=大神榜      
}

message GoldRankInfo  {
    uint32 headimg_id = 1;             //头像ID
    string username = 2;               //名字
    uint32 userid = 3;                 //玩家ID
    string num = 4;                    //金币数量 当是土豪榜时是【充值额】 是连赢榜时是【连赢局数】 是大神榜时是【奖金数额】
    uint32 rank_index = 5;             //名次排行 
}

message GetGoldRankListRes  {
    uint32 type = 1;                            //1=土豪榜 2=连赢榜 3=大神榜
    repeated GoldRankInfo gold_rank_list = 2;
    string update_time = 3;
    uint32 my_rank = 4;                         //我的排名 -1表示未上榜
}

message EnterRoomReq  {
    uint32 userid = 1;
    uint32 game_type = 2;
    uint32 room_level = 3;
    string token2 = 4;
}

message EnterRoomRes  {
    uint32 game_type = 1;
    uint32 room_level = 2;
    string room_link = 3;
    string token = 4;                   //进入房间的校验码
    string token2 = 5;
}


message AskJoinedGameInfoReq  {
    uint32 userid = 1;
}

message AskJoinedGameInfoRes  {
    bool has = 1;                      //是否加入过房间
    uint32 game_type = 2;
    uint32 room_level = 3;
    uint32 room_id = 4;                //好友房才有这个字段
    string ky_game_url = 5;           //开元棋牌才有这个字段
}


message MarqueeInfo {
    string content_msg = 1;         //播放内容
    repeated uint32 play_where = 2; //播放位置 1=大厅 2=选房 3=游戏
}

//每次最多推送2条，收到不覆盖，如果本地缓存还有没播放的跑马灯，则把新收到的拼接到后面
message MarqueeInfoNotify  {          
    repeated MarqueeInfo marqueeInfo = 1;
}

//活动请求
message ActionReq {
    uint32 userid = 1;
    string channel = 2;  //渠道
}

message ActionType {
    uint32 type = 1; //活动类型 1=普通活动 2=送礼金活动 3=成就活动 4=充值活动 5=大闯关
    string name = 2; //活动名称
    uint32 id   = 3; //活动id
    uint32 red_point = 4;   //红点 0没有 1有红点
}

//活动请求返回
message ActionRes {
    repeated ActionType action_type = 1;
}

message GetRewardNotify {
    uint32 id = 1; //复位的那个列表
}

message VipList {
    string vip_name = 1;    //VIP1、VIP2等
    uint32 vip_level = 2;   //VIP1时是1,VIP5时是5
    uint32 id = 3;          //当前等级对应的id
    uint32 coin = 4;        //当前等级可领取的礼金数
}

message GiftAction {
    repeated VipList vip_list = 1;      //vip列表
    uint32 vip_level = 2;               //当前玩家vip等级
    string surplus_coin = 3;            //剩下可领取的礼金
    string already_receive_coin = 4;    //已经领取的礼金
    uint32 cur_vip_id = 5;              //当前VIP等级的ID
}

message Achievement {
    uint32 id = 1;                      //成就id
    uint32 coin = 2;                    //成就奖金
    uint32 unlock_achievement_num = 3;  //总成就需求量
    uint32 unlock_num = 4;              //已经解锁的成就
    uint32 achievement_status = 5;      //状态(0=未完成,1=可领取,2=已领取)
}

message RechargeActivity {
    uint32 id = 1;              //子id
    string reward_gold = 2;     //奖金
    string to_get_num = 3;      //需要的金币
    string got_num = 4;         //已经充值的金币
    uint32 got_status = 5;      //状态(0=未完成,1=可领取,2=已领取)
}

/**
message SingleRechargeActivity {
    uint32 id = 1;              //子id
    string reward_gold = 2;     //单笔充值
    uint32 reward_type = 3;     //1是金币 2摇钱树 3全都有
    string gold_percentage = 4; //金币奖励比例
    uint32 tree_times = 5;      //摇钱树奖励次数
}
*/

//具体某个活动请求
message TheActionReq {
    uint32 userid = 1;
    uint32 type = 2;        //活动类型 1=送礼金活动 2=成就 3=普通 4=充值活动 7=大闯关 9=俱乐部邀请人数活动
    uint32 id   = 3;        //活动id
}

//有效投注大闯关
/**
message ValidBet {
    uint32 id = 1;              //子id
    string reward_gold = 2;     //单笔充值
    string to_get_num = 3;      //需要的条件数目
    string got_num = 4;         //已经得到的条件数目
    uint32 got_status = 5;      //状态(0=未完成,1=可领取,2=已领取)
    uint32 cid = 6;             //子游戏类型7001大闯关 7002捕鱼大闯关 7003电子大闯关
}*/

/** 每个俱乐部赠送的子项
message ClubSendActivity {
    uint32 id = 1;                      //活动id
    uint32 cur_player_nums = 2;         //俱乐部当前人数
    uint32 des_player_nums = 3;         //俱乐部目标人数
    uint32 reward_gold = 4;             //奖励金额
    uint32 got_status = 5;              //状态(0=未完成,1=可领取,2=已领取)
}
*/

/** 返水活动
message RebateActivity {
    repeated uint32 vip_info = 1;
    uint32 cur_vip = 2;
    string last_bet_coin = 3;   //昨日有效投注
    string surplus_coin = 4;    //剩下可领取的礼金
}*/

//具体某个活动返回(只会返回其中一个活动)
message TheActionRes {
    uint32 type = 1;                                            //活动类型 1=送礼金活动 2=成就 3=普通 4=充值活动 7=大闯关 9=俱乐部邀请人数活动
    uint32 id = 2;                                              //活动id
    string content = 3;                                         //普通活动内容
    string start_time = 4;                                      //开始的时间
    string end_time = 5;                                        //结束的时间 -1表示永久
    GiftAction gift_action = 6;                                 //送礼金活动返回
    repeated Achievement achievement = 7;                       //成就活动返回
    repeated RechargeActivity recharge_act = 8;                 //充值活动
    string json_data = 10;                                      //JSON格式的活动数据 对应每个活动
}

//领取签到奖励
message GetVipRewardReq {
    uint32 userid = 1;
    uint32 cur_vip_id = 2;              //当前VIP等级的ID
    uint32 id = 3;                      //活动ID
}

//领取签到奖励返回
message GetVipRewardRes {
    string already_receive_coin = 1;    //已经领取的礼金
    string gold = 2;                    //更新玩家金币
}

//领取成就奖励
message GetAchieveRewardReq {
    uint32 userid = 1;
    uint32 achieve_id = 2;
    uint32 id = 3;          //活动ID
}

//领取成就奖励返回
message GetAchieveRewardRes {
    uint32 achieve_id = 1;
    string gold = 2;                    //更新玩家金币
    uint32 status = 3;                  // 1=成功 0=失败
}

//领取活动奖励请求
message GetActionRewardReq {
    uint32 userid = 1;
    uint32 type = 2;    //活动类型
    uint32 id = 3;      //活动ID
    uint32 item_id = 4; //节点的ID号
}

//领取活动奖励返回
message GetActionRewardRes {
    uint32 type = 1;    //活动类型
    uint32 id = 2;      //活动ID
    uint32 item_id = 3;
    string gold = 4;    //更新玩家金币
    uint32 status = 5;  // 1=成功 0=失败(提示信息 : 活动数据错误,如有疑问请联系客服)
}

//游戏记录列表
message GetGameRecordListReq  {               
    uint32 userid = 1;
}

message GetGameRecordListRes  {
    repeated GameInfo game_record_list = 1;
}

//游戏记录详细信息
message GameRecordDetail  {
    string room_code = 1;                     //牌局编号
    string room_name = 2;                     //房间等级
    string score = 3;                         //得分
    uint32 conclude_time = 4;                 //结算时间
}

//请求游戏记录详情
message GetGameRecordDetailReq  {             //某个游戏记录详情
    uint32 userid = 1;
    uint32 game_type = 2;
}

message GetGameRecordDetailRes  {
    repeated GameRecordDetail game_record_detail = 1;
}

//推广信息请求
message PromoteMsgReq {
    uint32 userid = 1;
}

message PromoteMsgInfo {
    repeated string promote_msg = 1;
}

//推广信息返回
message PromoteMsgRes {
    repeated PromoteMsgInfo promote_msg_info = 1;//推广信息
    uint32 my_id = 2; //我的id
    uint32 superior_id = 3; //上级id
    string http_link = 4; //推广链接
}

//推广教程
message PromoteCourseReq {
    uint32 userid = 1;
    string channel = 2;  //渠道
}

//推广教程返回
message PromoteCourseRes {
    uint32 one_earnings = 1; // 一级收益比例 比如 25% 返回 25
    uint32 two_earnings = 2; // 二级收益比例 比如 15% 返回 15
    uint32 three_earnings = 3;// 三级收益比例 比如 5% 返回 5
}

//推广明细
message PromoteDetailReq {
    uint32 userid = 1;
    uint32 cur_page = 2; //当前页码
    uint32 count = 3; //请求当前页的记录数，默认请求10条
    uint32 data_type = 4; // 1=数据汇总 2=返利明细
    string time_start = 5; //开始时间
    string time_end = 6; //结束时间
}

message PromoteDetail {
    uint32 provide_id = 1;//提供佣金的玩家id
    uint32 superior_id = 2;//提供佣金的玩家的上级id
    string rebate_type = 3;//返利类型
    uint32 vip_level = 4;//vip等级
    string tax = 5;//税收
    string brokerage = 6; //佣金收入
}

//推广返回
message PromoteDetailRes {
    repeated PromoteDetail promote_detail = 1;
    uint32 data_type = 2; //0=今天 1=昨天 2=七天
    uint32 cur_page = 3; //当前页码
    uint32 total_page = 4; //总页码
    string reward_num = 5;      //推广赏金
    uint32 status = 6;      //可领取状态 1可领 2不可领
    string got_reward_num = 7; //累计领取的推广收益
}

message GoldDetialReq {
    uint32 userid = 1;
    uint32 cur_page = 2; //当前页码
    uint32 count = 3; //请求当前页的记录数，默认请求10条
    uint32 time_start = 4; //开始时间
    uint32 time_end = 5; //结束时间
    uint32 main_type = 6; //1游戏记录 2其他记录
    //游戏记录子类型: 0=全部 xxx=game_id
    //其他记录子类型 0=全部 1=转账 2=提现 3=充值 4=推广收益 5=活动奖励 9俱乐部
    uint32 sub_type = 7;
}

//一条详细数据
message GameDetial {
    string win_loss = 1; //盈亏
    uint32 the_time = 2; //结算时间
    string before_money = 3; //结算前金额
    string after_money = 4; //结算后金额
    string detial_type = 5; //类型 (初级房/ 转账入款)
    string pos = 6; //位置(牛牛/ 保险箱)
}

//金流信息返回
message GoldDetialRes {
    uint32 data_type = 1; //1=游戏记录 2=其他记录
    uint32 data_sub_type = 2; //子类型
    uint32 cur_page = 3; //当前页码
    uint32 total_page = 4; //总页码
    repeated GameDetial game_detial = 5; //游戏记录详细数据
}

message GetFriendGameInofReq  {
    uint32 userid = 1;
}

message GameDeployInfo  {
    GameInfo game_info = 1;                             //游戏名字
    repeated GameRoomInfo game_room_lists = 2;          //房间列表
}

message GetFriendGameInofRes  {
    repeated GameDeployInfo game_deploy_info = 1;
}

message FriendGameCreateReq  {
    EnterRoomReq friendgame_create = 1;
}

message FriendGameCreateRes  {
    EnterRoomRes friendgame_create_res = 1;
}

message FriendGamePrePareJoinReq  {
    uint32 userid = 1;
