}

//广告
message Advertisement {
    uint32 ad_id = 1; //广告id
    string image_url = 2;  
    uint32 is_show = 3; //展示(0-否,1-是) 
    string copy_expand_Link = 4; //复制推广链接
    string copy_appoint_Link = 5; //复制指定链接
    uint32 game_type = 6;          //跳转到指定游戏的游戏类型
