syntax = "proto3";
package WZRY;



message MsgBody {
    GameInOrderNotify   game_in_order_notify    = 1;    //游戏就绪通知

    GameInitReq         game_init_req           = 2;    //游戏初始化请求
    GameInitRes         game_init_res           = 3;    //游戏初始化返回结果

    GameBaseReq         game_base_req           = 4;    //请求游戏开始
    GameBaseRes         game_base_res           = 5;    //普通/免费环节游戏结果

    GameBonusBetReq     game_bonus_bet_req      = 6;    //bonus环节
    GameBonusBetRes     game_bonus_bet_res      = 7;    //bonus环节结果

    GameReconnectInfo   game_reconnect_info     = 8;

    GameErrRes          game_err_res            = 9;   //游戏内部错误
}

message RoomInfo {
    string room_code = 1;             //房间编号信息
}

message PlayerInfo {
    uint32 player_id = 1;
    uint32 seat_id = 2;      //座位ID
    string username = 3;
    float gold = 4;
    uint32 headimg_id = 5;
    uint32 sex = 6;          //性别
}

message GameInOrderNotify {
    uint32 player_id = 1;
}

message GameInitReq {
    uint32 player_id = 1;
}

message BetInfo {
    uint32 bet_id = 1;      //下注ID
    uint32 bet_value = 2;   //下注值
}

message GameInitRes {
    PlayerInfo player_info = 1; //当前玩家
    RoomInfo room_info = 2;
    repeated BetInfo bet_info = 3;//玩家可下注的值
    uint32 jp_coll_left_time = 4;//铭文收集剩余时间
    uint32 jp_coll_count = 5;   //铭文已收集数量
    uint32 free_time_left = 6;  //剩余几次免费
    uint32 last_bet_index = 7;  //上次下注id
    uint32 last_bet_line_count = 8;  //上次下注线数
    float last_win = 9;        //上局中奖钱数
    uint32 cur_game_station = 10;    //当前游戏状态
    string cur_can_bet_money = 11; // 如果是红蓝对决，多少钱。
}

message GameBaseReq {
    uint32 cur_bet_index = 1;   //当前下注id
    uint32 cur_bet_line_count = 2;  //当前下注线数
}

message PicInfo {
    uint32 pic_id = 1;          //图片id
    bool is_conn = 2;           //是否中奖
}

message RollInfo {          //每一行的滚轮图片
    repeated PicInfo pic_info = 1; //滚轮每一行的图片组
}

message LineConnectInfo {
    uint32 pic_id = 1;      //中奖图片
    uint32 pic_count = 2;   //中奖图片个数
    uint32 line_id = 3;     //第几条线
}

message GameBaseRes {
    PlayerInfo player_info = 1;         //玩家信息
    uint32 cur_game_station = 2;        //当前游戏状态 1普通 2免费 3bonus
    uint32 free_time_left = 3;          //剩余几次免费
    uint32 next_game_station = 4;       //下一回合游戏状态
    bool is_win_free = 5;               //本次是否中了免费
    repeated RollInfo roll_info = 6;    //当前滚轮对应图片
    repeated LineConnectInfo line_connect_info = 7;     //连线情况
    float total_win = 8;                //本回合赢钱
    uint32 cur_win_type = 9;            //当前赢的类型 NORMAL_WIN = 1 AMAZING_WIN = 2 BIG_WIN = 3
    uint32 cur_jackpot_count = 10;      //当前已收集的铭文数量
    uint32 jackpot_get = 11;            //本回合收集到的铭文数量
    float cur_jackpot_win = 12;         //当前jackpot赢的钱
    uint32 jackpot_left_time = 13;      //jackpot收集剩余时间
    float start_money = 14;             //游戏开始时的金币数
}

message GameBonusBetReq {
    bool is_bet = 1;                    //是否下注
    uint32 bet_index = 2;               //下注区域 1红方 2蓝方 3平
}

message GameBonusBetRes {
    uint32 cur_bet_index = 1;           //当前下注区域 1红方 2蓝方 3平
    uint32 red_pic_id = 2;              //红方开的图片
    string red_pic_money = 3;           //红方开的图片的钱
    uint32 blue_pic_id = 4;             //蓝方开的图片
    string blue_pic_money = 5;          //蓝方开的图片的钱
    uint32 bet_result = 6;              //下注结果 1红方 2蓝方 3平
    float cur_can_bet_money = 7;        //当前可继续下注的钱 当主动选择不再下注的时候 该值为最终bonus环节赢的钱数
    uint32 next_game_station = 8;       //接下来的游戏状态
    uint32 jackpot_left_time = 9;       //jackpot收集剩余时间
    PlayerInfo player_info = 10;        //下注结束后 玩家信息
}

message GameReconnectInfo{
    PlayerInfo player_info = 1;         //玩家信息
    uint32 cur_game_station = 2;        //当前游戏状态
    uint32 free_time_left = 3;          //当前剩余几次免费
    GameBonusBetRes bonus_bet_res = 4;  //bonus环节游戏结果 若当前环节非bonus 则信息内容为空
    uint32 jp_coll_count = 5;           //铭文已收集数量
    uint32 jp_coll_left_time = 6;       //铭文收集剩余时间
}

message GameErrRes {
    uint32 err_id = 1;      //错误码 1:钱不足 2:状态错误 3:无效下注索引 4:无效下注线数
}
