syntax = "proto3";
package Club;


message MsgBody  {

    ClubCreateReq       club_create_req = 1;                  //俱乐部创建请求
    ClubCreateRes       club_create_res = 2;                  //俱乐部创建返回

    ClubJoinReq         club_join_req = 3;                    //俱乐部加入请求
    ClubJoinRes         club_join_res = 4;                    //俱乐部加入返回

    ClubListReq         club_list_req = 5;                  //俱乐部列表请求
    ClubListRes         club_list_res = 6;                  //俱乐部列表返回

    ClubSearchReq       club_search_req = 7;                  //俱乐部搜索请求
    ClubSearchRes       club_search_res = 8;                  //俱乐部搜索返回

    ClubReNameReq       club_re_name_req = 9;                 //俱乐部重命名请求
    ClubReNameRes       club_re_name_res = 10;                 //俱乐部重命名返回

    ClubCancelReq       club_cancel_req = 11;                 //俱乐部解散请求
    ClubCancelRes       club_cancel_res = 12;                 //俱乐部解散返回

    ClubQuitReq         club_quit_req = 13;                  //玩家申请退出俱乐部请求
    ClubQuitRes         club_quit_res = 14;                  //玩家申请退出俱乐部返回

    ClubReConfigReq    club_re_config_req = 15;               //俱乐部重置配置(公告、是否需要审核等需求)请求
    ClubReConfigRes    club_re_config_res = 16;               //俱乐部重置配置(公告、是否需要审核等需求)返回

    ClubAddLeagueReq      club_add_league_req = 17;             //俱乐部加入联盟请求
    ClubAddLeagueRes      club_add_league_res = 18;             //俱乐部加入联盟返回

    ClubQuitLeagueReq      club_quit_league_req = 19;             //俱乐部退出联盟请求
    ClubQuitLeagueRes      club_quit_league_res = 20;             //俱乐部退出联盟返回

    ClubMembersListReq      club_members_list_req = 21;           //俱乐部成员列表请求
    ClubMembersListRes      club_members_list_res = 22;           //俱乐部成员列表返回

    ClubDeleteMemberReq      club_delete_member_req = 23;           //俱乐部删除成员请求
    ClubDeleteMemberRes      club_delete_member_res = 24;           //俱乐部删除成员返回

    ClubReSetManagerReq      club_re_set_manager_req = 25;           //俱乐部重置管理员请求
    ClubReSetManagerRes      club_re_set_manager_res = 26;           //俱乐部重置管理员返回

    ClubPassRefuseReq      club_pass_refuse_req = 27;                //俱乐部 通过/拒绝 加入请求
    ClubPassRefuseRes      club_pass_refuse_res = 28;                //俱乐部 通过/拒绝 加入返回

    ClubNeedCheckerListReq      club_need_checker_list_req = 29;        //待审核玩家列表请求
    ClubNeedCheckerListRes      club_need_checker_list_res = 30;        //待审核玩家列表请求返回


    ClubListAutoPushRes      club_list_auto_push_res = 31;              //俱乐部列表自动推送
    MembersListAutoPushRes   members_list_auto_push_res = 32;           //成员列表自动推送

    ClubInviteReq               club_invite_req = 33;               //俱乐部邀请玩家请求
    ClubInviteRes               club_invite_res = 34;               //俱乐部邀请玩家返回


    ClubProfitReq               club_profit_req = 35;               //俱乐部收益列表请求
    ClubProfitRes               club_profit_res = 36;               //俱乐部收益列表返回

    ClubGetProfitReq            club_get_profit_req = 37;           //俱乐部领取收益请求
    ClubGetProfitRes            club_get_profit_res = 38;           //俱乐领取部收益返回




    ClubGameListReq    club_game_list_req = 50;            //俱乐部游戏列表请求
    ClubGameListRes    club_game_list_res = 51;            //俱乐部游戏列表返回
    ClubRoomListReq    club_room_list_req = 52;            //俱乐部房间列表请求
    ClubRoomListRes    club_room_list_res = 53;            //俱乐部房间列表返回

    ClubRoomAutoPushRes    club_room_auto_push_res = 54;    //俱乐部房间推送,单个房间推送,房间变动,玩家变动都会推送
    
    
    GetCreateRoomInfoReq get_create_roominfo_req = 60;     //创建房间时 询问房间的参数
    GetCreateRoomInfoRes get_create_roominfo_res = 61; 

    CreateClubRoomReq    create_clubroom_req = 62;         //创建俱乐部房间请求
    CreateClubRoomRes    create_clubroom_res = 63; 

    JointClubRoomReq     join_clubroom_req = 64;           //加入俱乐部房间请求
    JointClubRoomRes     join_clubroom_res = 65; 
    
}

message ClubInfo {
    uint32 club_id = 1;             //俱乐部id
    string club_name = 2;           //俱乐部名称
    uint32 club_members = 3;        //俱乐部成员数
    uint32 open_rooms = 4;          //俱乐部当前开房数
    uint32 club_state = 5;          //俱乐部状态 0=未申请、1=已申请、2=未满、3=已满
    uint32 add_league = 6;          //是否加入联盟 1=是； 0=否
    uint32 join_type = 7;           //玩家加入该俱乐部的方式，1=允许所有人加入，2=审核后才可加入
    string announcement = 8;        //俱乐部公告
    int32 create_user_id = 9;       //俱乐部创始人id
    uint32 identity_type = 10;       //职务标签 1=创建人、2=管理员、3=普通成员
    uint32 quit_time = 11;           //退出时间撮，如果玩家申请退出当前俱乐部，则存在倒计时
    string club_invite_link = 12;    // 俱乐部邀请链接 www.baidu.com?create_user_id?club_id
    int32 exist_checked_show_red = 13;   //存在需要审核的玩家,显示 小红点 1=存在 0=不存在
    int32 red_bot = 14; //是否存在小红点 0=不存在 1=存在，不做推送
}

//俱乐部创建请求
message ClubCreateReq {
    uint32 user_id = 1;             //玩家id
    string club_name = 2;           //俱乐部名称
}

//俱乐部创建返回
message ClubCreateRes {
    uint32 state = 1;                   // 0=俱乐部名称已经存在、1=创建成功 2=其它失败原因 ...
    string other_msg  = 2;              // 预留，如果以后中文提示服务器做，可以放到这里
    ClubInfo club_info = 3;             //当前加入的俱乐部信息
}

//俱乐部加入请求
message ClubJoinReq {
    uint32 user_id = 1;             //玩家id
    uint32 club_id = 2;             //俱乐部id（默认根据id加入，如果id没有我会判断名称是否存在，如果存在根据名称加入）
    string club_name = 3;           //俱乐部名称(保留字段，以防以后加一个根据名称加入的需求)
}

//俱乐部加入请求
message ClubJoinRes {
    uint32 state = 1;                   // 0=俱乐部id不存在、1=俱乐部名称不存在、2=加入成功 3=加入失败 4=等待审核 5=已加入...
    string other_msg  = 2;              // 预留，如果以后中文提示服务器做，可以放到这里
    ClubInfo club_info = 3;             //当前加入的俱乐部信息
}

//俱乐部列表请求
message ClubListReq {
    uint32 user_id = 1;             //玩家id
    uint32 cur_page  = 2;           // 当前页码
    uint32 page_size = 3;           // 页码大小
}


//俱乐部配置
message ClubConfig {
    uint32 add_league_limit = 1;            // 加入联盟的最少人数
    uint32 cancel_room_time  = 2;           // 多少时间没有玩游戏解散房间
    uint32 cancel_club_less_than = 3;       // less_than 小于多少玩家，且 un_game_day 几天没人玩，解散俱乐部。
    uint32 cancel_club_un_game_day = 4;     // less_than 小于多少玩家，且 un_game_day 几天没人玩，解散俱乐部。
    uint32 retain_day = 5;                  // 收益保留多少天，如果超过这个天数，就不能再领取
}

 //俱乐部列表返回
message ClubListRes {
    repeated ClubInfo club_info = 1;
    uint32 totals = 2;                      //总条数,根据总条数计算出总页码
    ClubConfig club_config = 3;
    uint32 invite_type = 4;                 //是否是二维码邀请  1=二维码邀请  空或者其它都不是
}

//俱乐部搜索请求
message ClubSearchReq {
    uint32 user_id = 1;                 //玩家id
    uint32 club_id = 2;                 //根据俱乐部id搜索
    string club_name  = 3;              //根据俱乐部名称搜索
    uint32 cur_page  = 4;               // 当前页码
    uint32 page_size = 5;               // 页码大小
}

 //俱乐部搜索返回
 message ClubSearchRes {
    repeated ClubInfo club_info = 1;
    uint32 totals = 2;      //总条数,根据总条数计算出总页码
}

//俱乐部重命名请求
message ClubReNameReq {
    uint32 user_id = 1;                 //玩家id(一般是创始人才能重命名)
    uint32 club_id = 2;                 //俱乐部id
    string club_name  = 3;              //新俱乐部名称
}

//俱乐部重命名返回
message ClubReNameRes {
    uint32 state = 1;                   // 0=修改失败、1=修改成功
    string other_msg  = 2;              // 预留，如果以后中文提示服务器做，可以放到这里
    uint32 club_id = 3;                 //俱乐部id
    string club_name  = 4;              //新俱乐部名称
}

//俱乐部解散请求
message ClubCancelReq {
    uint32 user_id = 1;         //玩家id(一般是创始人才能解散)
    uint32 club_id = 2;         //俱乐部id
}

//俱乐部解散返回
message ClubCancelRes {
    uint32 state = 1;                   // 0=解散失败、1=解散成功
    string other_msg  = 2;              // 预留，如果以后中文提示服务器做，可以放到这里
    uint32 club_id = 3;                 //俱乐部id
}

//俱乐部退出请求
message ClubQuitReq {
    uint32 user_id = 1;         //玩家id(一般创始人不能退出)
    uint32 club_id = 2;         //俱乐部id
    uint32 stop_quit = 3;       //1 是取消退出 0是退出
}

//俱乐部退出返回
message ClubQuitRes {
    uint32 state = 1;                   // 0=退出失败、1=退出成功 2=等待退出(等待时间到了会再次推送这个协议)
    uint32 club_id = 2;                 //俱乐部id
    string other_msg  = 3;              // 预留，如果以后中文提示服务器做，可以放到这里
    uint32 quit_time = 4;               //退出时间撮
}

//俱乐部重置配置(公告、是否需要审核等需求)请求
message ClubReConfigReq {
    uint32 user_id = 1;                 //玩家id(一般创始人才能修改)
    uint32 club_id = 2;                 //俱乐部id
    uint32 join_type = 3;               //1=允许所有人加入，2=审核后才可加入
    string announcement = 4;            //俱乐部公告
}

//俱乐部重置配置(公告、是否需要审核等需求)返回
message ClubReConfigRes {
    uint32 state = 1;                   // 0=重置失败、1=重置成功
    uint32 club_id = 2;                 //俱乐部id
    uint32 join_type = 3;               //1=允许所有人加入，2=审核后才可加入
    string announcement = 4;            //俱乐部公告
    string other_msg  = 5;              // 预留，如果以后中文提示服务器做，可以放到这里
}

//申请加入联盟
message ClubAddLeagueReq {
    uint32 user_id = 1;         //玩家id(一般创始人才能操作)
    uint32 club_id = 2;         //俱乐部id
}

//申请加入联盟返回
message ClubAddLeagueRes {
    uint32 state = 1;                   // 0=加入失败、1=加入成功
    uint32 club_id = 2;                 //俱乐部id
    string other_msg  = 3;              // 预留，如果以后中文提示服务器做，可以放到这里
}

//申请退出联盟
message ClubQuitLeagueReq {
    uint32 user_id = 1;         //玩家id(一般创始人才能操作)
    uint32 club_id = 2;         //俱乐部id
}

//申请退出联盟返回
message ClubQuitLeagueRes {
    uint32 state = 1;                   // 0=退出失败、1=退出成功
    uint32 club_id = 2;                 //俱乐部id
    string other_msg  = 3;              // 预留，如果以后中文提示服务器做，可以放到这里
}

message Members {
    uint32 user_id = 1;
    string nick_name  = 2;
    string img_url  = 3;
    int32 online  = 4;             // 0=离线 1=在线
    string gold    = 5;             //携带金币
    uint32 identity_type = 6;       //职务标签 1=创建人、2=管理员、3=普通成员
    int32 vip_level  = 7;           // vip等级
}

//请求成员列表
message ClubMembersListReq {
    uint32 user_id = 1;                 //玩家id，如果存在，只查询单个玩家记录
    uint32 club_id = 2;                 //俱乐部id
    uint32 cur_page  = 3;               // 当前页码
    uint32 page_size = 4;               // 页码大小
}

//请求成员列表返回
message ClubMembersListRes {
    repeated Members members = 1;         //成员列表
    uint32 totals = 2;                  //总条数,根据总条数计算出总页码
}

//删除成员请求
message ClubDeleteMemberReq {
    uint32 user_id = 1;                     //玩家id(一般创始人才能操作)
    uint32 club_id = 2;                 //俱乐部id
    repeated uint32 user_ids = 3;           //被删除的玩家id
}

//删除成员返回
message ClubDeleteMemberRes {
    uint32 state = 1;                   // 0=失败、1=成功
    string other_msg  = 2;              // 预留，如果以后中文提示服务器做，可以放到这里
    repeated Members members = 3;      //成员列表
}

//重置管理员
message ClubReSetManagerReq {
    uint32 user_id = 1;             //玩家id(一般创始人才能操作)
    uint32 club_id = 2;             //俱乐部id
    uint32 identity_type = 3;       //职务标签 2=管理员(设置)、3=普通成员(取消管理员)
    repeated uint32 user_ids = 4;          //数组形式，不管是1人还是多人[110000012,12423012,...]
}

//重置管理员返回
message ClubReSetManagerRes {
    uint32 state = 1;                   // 0=失败、1=成功
    uint32 club_id = 2;                 //俱乐部id
    string other_msg  = 3;              // 预留，如果以后中文提示服务器做，可以放到这里
}

//通过或者拒绝玩家加入
message ClubPassRefuseReq {
    uint32 user_id = 1;                     //玩家id(一般创始人才能操作)
    uint32 club_id = 2;                     //俱乐部id
    uint32 type = 3;                        // 0 = 拒绝 1=通过
    repeated uint32 user_ids = 4;          //数组形式，不管是1人还是多人[110000012,12423012,...]
}

//通过或者拒绝玩家加入返回
message ClubPassRefuseRes {
    uint32 state = 1;                   // 0=失败、1=成功
    string other_msg  = 2;              // 预留，如果以后中文提示服务器做，可以放到这里
    repeated Members members = 3;         //成员列表
}

//待审核玩家列表
message ClubNeedCheckerListReq {
    uint32 user_id = 1;                     //玩家id(一般创始人才能操作)
    uint32 club_id = 2;                     //俱乐部id
    uint32 cur_page  = 3;               // 当前页码
    uint32 page_size = 4;               // 页码大小
}

//待审核玩家列表
message ClubNeedCheckerListRes {
    repeated Members members = 1;         //成员列表
    uint32 totals = 2;                  //总条数,根据总条数计算出总页码
}

//俱乐部列表推送
message ClubListAutoPushRes {
    ClubInfo club_info = 1;
    uint32 modify_type = 2;         // 1 = 修改、 2 = 添加、 3 = 删除
    string other_msg  = 3;              // 预留，如果以后中文提示服务器做，可以放到这里
}

//成员列表推送
message MembersListAutoPushRes {
    uint32 club_id = 1;                     //俱乐部id
    repeated Members members = 2;           //成员列表
    uint32 modify_type = 3;                 // 1 = 修改、 2=添加、 3 = 删除
    string other_msg  = 4;              // 预留，如果以后中文提示服务器做，可以放到这里 
}

//邀请玩家加入俱乐部（扫码、链接、手动邀请，都用这个）
message ClubInviteReq {
    uint32 user_id = 1;                     //请求人id
    uint32 club_id = 2;                     //俱乐部id
    uint32 invited_user_id = 3;             //被邀请玩家id(这个id有可能和上面的user_id一样)
    uint32 create_user_id = 4;              //创建人id  
    uint32 invite_type = 5;                 //邀请类型 0(或者空)=手动输入id邀请    1=二维码邀请     
}

message ClubInviteRes {
    uint32 state = 1;                   // 0=失败、1=成功
    string other_msg  = 2;              // 预留，如果以后中文提示服务器做，可以放到这里
    uint32 club_id = 3;                 //俱乐部id
}


//俱乐部收益列表请求
message ClubProfitReq {
    uint32 user_id = 1;
    uint32 club_id = 2;                 //俱乐部id
    uint32 cur_page  = 3;               // 当前页码
    uint32 page_size = 4;               // 页码大小
}

message ProfitList {
    uint32 id = 1;
    uint32 type = 2;            //类型 收益类型 1=俱乐部房收益，2=联盟房收益
    string card_num = 3;        //牌局编号
    uint32 user_id = 4;         //玩家id
    string win_score = 5;       //赢金额
    string profit = 6;          //收益
    uint32 end_time = 7;        //结算时间
    uint32 state = 8;           //状态  1=已领取 0=未领取
}

//俱乐部收益列表返回
message ClubProfitRes {
    repeated ProfitList profit_list = 1;
    string got_profit = 2;          //已领取收益
    string not_get_profit = 3;      //未领取收益，如果小于0，领取按钮置灰
    uint32 totals = 4;              //总条数,根据总条数计算出总页码
}


//俱乐部领取收益请求
message ClubGetProfitReq {
    uint32 user_id = 1;                 //一般是创始人才能领取
    uint32 club_id = 2;                 //俱乐部id
}

//俱乐部领取收益请求返回
message ClubGetProfitRes {
    uint32 state = 1;                   // 0=失败、1=成功
    string other_msg  = 2;              // 预留，如果以后中文提示服务器做，可以放到这里
    uint32 club_id = 3;                 //俱乐部id
    string got_profit = 4;          //已领取收益
    string not_get_profit = 5;      //未领取收益，如果小于0，领取按钮置灰
}


//游戏列表请求
message ClubGameListReq {
    uint32 user_id = 1;                     //玩家id
    uint32 club_id = 2;                     //俱乐部id
}

message ClubGameList {
    uint32 game_id = 1;              //游戏id
    string game_name = 2;            //游戏名称
    string game_tag = 3;             //游戏标签 json数组格式，[1,3,4]  1=全部游戏 2=牛牛游戏  3=炸金花游戏 4 = 纸牌游戏 5=麻将游戏 6=百人游戏
}

//游戏列表请求返回
message ClubGameListRes {
    uint32 club_id = 1;                     //俱乐部id
    repeated ClubGameList club_game_list = 2;
}

message ClubRoomListReq  {
    uint32 club_id = 1;             //俱乐部id
    uint32 room_type = 2;           // 1=俱乐部房、 2=联盟房
    uint32 game_type = 3;           // 0=全部游戏、 1=抢庄牛牛、 ... 
    uint32 cur_page  = 4;           // 当前页码
    uint32 page_size = 5;           // 页码大小
}

message PlayerInfo {
    uint32 player_id = 1;
    uint32 seat_id = 2; //座位ID
    string username = 3;
    string gold = 4;
    uint32 headimg_id = 5;
    uint32 sex = 6; //性别
    uint32 vip_level = 7; //vip等级
}

message ClubRoomList {
    uint32 game_id = 1;                         //游戏id
    string game_name = 2;                       //游戏名称
    string u_room_id = 3;                       //房间唯一id,游戏id拼接上时间(防重复)
    uint32 max_users = 4;                       //房间支持的最大人数
    uint32 current_users = 5;                   //房间的当前人数
    string base_score = 6;                      //底注
    uint32 limit_min_red = 7;                   //最小限红
    uint32 limit_max_red = 8;                   //最大限红
    string limit_enter  = 9;                    //准入
    uint32 game_state  = 10;                    //是否开始，1=开始游戏 0=未开始游戏
    repeated PlayerInfo player_Info = 11;
}

message ClubRoomListRes  {
    uint32 club_id = 1;                              //俱乐部id
    repeated ClubRoomList club_room_list = 2;        //房间列表
    uint32 totals = 3;              //总条数,根据总条数计算出总页码
}


//俱乐部房间推送,单个房间推送,房间变动,玩家变动都会推送
message ClubRoomAutoPushRes {
    ClubRoomList club_room_list = 1;
    uint32 modify_type = 2;             // 1 = 修改、 2 = 添加、 3 = 删除
    string other_msg  = 3;              // 预留，如果以后中文提示服务器做，可以放到这里
}


message GetCreateRoomInfoReq  {
    uint32 club_id = 1;                 //俱乐部id
    uint32 game_id = 2;                 //游戏id
}

message GetCreateRoomInfoRes  {
    repeated string room_base_note = 1;          //底注
    repeated string room_min_entrance = 2;       //准入
    repeated uint32 player_num = 3;              //人数
    repeated uint32 show_area = 4;               //1全部 2俱乐部 3联盟
}


message CreateClubRoomReq  {
    uint32 user_id = 1;
    uint32 club_id = 2;                         //俱乐部id
    uint32 game_id = 3;
    uint32 level_index = 4;                     //选择底注的index 服务器用这个来作为房间等级
    string room_base_note = 5;                  //底注
    string room_min_entrance = 6;               //准入
    uint32 player_num = 7;                      //人数
    uint32 show_area = 8;                       //1全部 2俱乐部 3联盟
}

message CreateClubRoomRes  {
    uint32 game_id = 1;
    string room_link = 2;                       //进入房间的地址
    string token = 3;                           //进入房间的校验码
    uint32 room_level = 4;                      //返回的房间等级
}


message JointClubRoomReq  {
    uint32 user_id = 1;
    uint32 club_id = 2;                         //俱乐部id
    uint32 room_id = 3;
}

message JointClubRoomRes  {
    uint32 game_id = 1;
    string room_link = 2;                       //进入房间的地址
    string token = 3;                           //进入房间的校验码
    uint32 room_level = 4;                      //返回的房间等级
}
