syntax = "proto3";
package SGJ;

//游戏状态 1恢复正常 2猜大小

message MsgBody {
    GameInOrderNotify   game_in_order_notify    = 1;    //游戏就绪通知

    GameInitReq         game_init_req           = 2;    //游戏初始化请求
    GameInitRes         game_init_res           = 3;    //游戏初始化返回结果

    GameStartReq        game_start_req          = 4;    //请求游戏开始
    GameStartRes        game_start_res          = 5;    //游戏结果

    GameChangeScoreReq  game_change_score_req   = 6;    //改变得分栏
    GameChangeScoreRes  game_change_score_res   = 7;    //改变得分栏结果

    GameBonusReq        game_bonus_req          = 8;    //bonus猜大小环节 
    GameBonusRes        game_bonus_res          = 9;    //bonus猜大小环节结果

    GameErrRes          game_err_res            = 10;   //游戏内部错误
}

message RoomInfo {
    string room_code = 1;               //房间编号信息
}

message PlayerInfo {
    uint32 player_id = 1;
    uint32 seat_id = 2;                 //座位ID
    string username = 3;
    string gold = 4;
    uint32 headimg_id = 5;
    uint32 sex = 6;                     //性别
}

message GameInOrderNotify {
    uint32 player_id = 1;
}

message GameInitReq {
    uint32 player_id = 1;
}

message BetInfo {
    uint32 fruit_id = 1;    //下注的水果ID
    uint32 fruit_bet = 2;   //当前水果倍数
    uint32 bet_value = 3;   //下注值 有值则有下注值 无则0
}

//初始化游戏界面
//轮盘位置信息 由左上角从0开始 顺时针
message GameInitRes {
    PlayerInfo player_info = 1;         //当前玩家
    RoomInfo room_info = 2;             //房间信息
    repeated float single_bet_info = 3; //单注下注数组
    repeated BetInfo last_bet_info = 4; //上一局玩家下注的内容
    uint32 single_bet = 5;              //上一局单注选择数值
    uint32 last_stop_idx = 6;           //上一局水果停留位置
    repeated uint32 lucky_idx_arr = 7;  //中了Lucky 9位蓝色LUCKY 21为彩色LUCKY 无则空数组
    string score_value = 8;             //当前得分栏数值 无则0
    uint32 bonus_value = 9;             //当前猜大小框内的数字 无则0
    uint32 game_station = 10;           //当前游戏状态 1恢复正常 2猜大小
    repeated uint32 record_list = 11;   //最新的水果机战绩 最多六局
}

message GameStartReq {
    repeated BetInfo bet_info = 1;      //玩家的下注值
    uint32 single_bet = 2;              //单注选择数值
}

message GameStartRes {
    string gold = 1;                    //游戏开始后 暂扣金币 刷新用
    uint32 stop_idx = 2;                //水果停留位置
    repeated uint32 lucky_idx_arr = 3;  //中了Lucky 9位蓝色LUCKY 21为彩色LUCKY 无则空数组
    repeated uint32 win_fruit_id = 4;   //中奖的水果下注ID的数组 LUCKY环节可能有多个中奖下注ID组
    string score_value = 5;             //本局得分栏数值 无则0
    uint32 win_type = 6;                //赢钱类型 常规中奖为1 特殊中奖为2
    uint32 next_game_station = 10;      //转盘结束状态 1恢复正常 2猜大小
}

message GameChangeScoreReq {
    bool is_get_score = 1;              //是否点击获取得分
    bool is_add = 2;                    //是否选择增加 如果is_get_score为true 即表明获取得分时这个字段无效
}

message GameChangeScoreRes {
    string gold = 1;                    //玩家金币
    string score_value = 2;             //当前得分栏数值
    uint32 next_game_station = 3;       //下一回合游戏状态 1恢复正常 2猜大小
}

message GameBonusReq {
    uint32 bet_pos = 1;                 //猜的位置 小为1 大为2
}

message GameBonusRes {
    bool is_win = 1;                    //是否赢
    uint32 bonus_value = 2;             //押注的结果数值 1~14
    string score_value = 3;             //猜大小后得分栏数值
    uint32 next_game_station = 4;       //下一回合游戏状态 1恢复正常 2猜大小
}

message GameErrRes {
    uint32 err_id = 1;      //错误码 1:钱不足 2:状态错误 3:无效下注索引 4:无效下注线数
}
