syntax = "proto3";
package MFWZ;

message MsgBody {
  GamePlayerMoveReq       game_player_move_req      = 1;    //玩家移动请求
  GamePlayerMoveNotify    game_player_move_notify   = 2;    //玩家移动通知

  GameBulletCreateReq     game_bullet_create_req    = 3;    //子弹生成请求
  GameBulletCreateRes     game_bullet_create_res    = 4;    //子弹生成回应
  GameBulletDisappearReq  game_bullet_disappear_req = 5;    //子弹消失请求
  GameBulletDisappearRes  game_bullet_disappear_res = 6;    //子弹消失回应
  GameBulletAttackReq     game_bullet_attack_req    = 7;    //子弹攻击请求
  GameBulletChangeReq     game_bullet_change_req    = 8;    //改变子弹等级请求
  GameBulletChangeRes     game_bullet_change_res    = 9;    //改变子弹等级回应

  GameFishFreshNotify     game_fish_fresh_notify    = 10;   //鱼刷新通知
  GameFishDiedNotify      game_fish_died_notify     = 11;   //鱼死亡通知

  GameReconnectInfo       game_reconnect_info       = 12;

  GameOtherJoin           game_other_join           = 13;   //其他玩家加入
  GameOtherLeave          game_other_leave          = 14;   //其他玩家退出
}

message RoomInfo {
  string room_code = 1;             //房间编号信息
}

message PlayerInfo {
  uint32 player_id = 1;
  uint32 seat_id = 2;      //座位ID
  string username = 3;
  float gold = 4;
  uint32 headimg_id = 5;
  uint32 sex = 6;          //性别
  Point point = 7;          //坐标
  uint32 bullet_cur_level = 8;  //子弹等级 用于皮肤初始化
  uint32 angle = 9;                 //人物角度 正右侧为0度 逆时针角度增加
  uint32 vip_level = 10; //用户VIP等级
}

message GameInOrderNotify {
  uint32 player_id = 1;
}

//游戏初始化请求
message GameInitReq {
  uint32 player_id = 1;
}

message BetInfo {
  uint32 bet_id = 1;      //下注ID
  uint32 bet_value = 2;   //下注值
}

//坐标
message Point {
  float x = 1;                      //x坐标
  float y = 2;                      //y坐标
}

//玩家移动请求
message GamePlayerMoveReq {
  uint32 player_id = 1;             //玩家id
  uint32 state = 2;                 //移动状态 0表示停止 1表示移动
  uint32 angle = 3;                 //角度 正右侧为0度 逆时针角度增加
  Point point = 4;                  //坐标
}

//玩家移动通知
message GamePlayerMoveNotify {
  uint32 player_id = 1;             //玩家id
  uint32 state = 2;                 //移动状态 0表示停止 1表示移动
  uint32 angle = 3;                 //角度 正右侧为0度 逆时针角度增加
  Point point = 4;                  //坐标
  uint32 seat_id = 5;               //移动的玩家seatid
}

//子弹生成请求
message GameBulletCreateReq {
  uint32 player_id = 1;             //玩家id
  Point point = 2;                  //坐标
  uint32 angle = 3;                 //角度 正右侧为0度 逆时针角度增加
  uint64  time = 4;                   //时间
}

//子弹生成回应
message GameBulletCreateRes {
  uint32 res = 1;                   //射击子弹结果 0成功 1下注无效 2金币不足 3屏幕中该玩家的子弹过多
  uint32 bullet_id = 2;             //子弹id 成功时返回id 失败返回0
  Point point = 3;                  //坐标
  string gold = 4;                  //当前金币
  uint32 seat_id = 5;               //
  uint32 speed = 6;                 //子弹速度
  Point point_player = 7;           //人物最新坐标坐标
}

//子弹消失请求
message GameBulletDisappearReq {
  uint32 player_id = 1;             //玩家id
  uint32 bullet_id = 2;             //子弹id
}

//子弹消失回应
message GameBulletDisappearRes {
  uint32 res = 1;                   //结果 0成功
  string gold = 2;                  //当前金币
}

//子弹攻击请求
message GameBulletAttackReq {
  uint32 player_id = 1;             //玩家id
  uint32 bullet_id = 2;             //子弹id
  uint32 fish_id = 3;               //鱼id
  uint64  time = 4;                 //时间
  bool is_my_bullet = 5;            //是否是自己的子弹
}

//改变子弹等级请求
message GameBulletChangeReq {
  uint32 player_id = 1;             //玩家id
  uint32 level = 2;                 //要切换到的目标子弹等级
}

//改变子弹等级回应
message GameBulletChangeRes {
  uint32 res = 1;                   //结果 0成功 1目标子弹等级错误
  uint32 player_id = 2;             //切换子弹的玩家
  uint32 seat_id = 3;               //切换子弹的玩家
  uint32 level = 4;                 //要切换到的目标子弹等级
}

//子弹攻击回应
message GameBulletAttackRes {
  uint32 res = 1;                   //结果 0成功 1未打中
}

message FishInfo {
  uint32 fish_id = 1;               //鱼id
  uint32 fish_type = 2;             //鱼种类
}

//鱼刷新通知
message GameFishFreshNotify {
  repeated FishInfo fish_info = 1;  //鱼信息
  uint32 count = 3;                 //刷新数量
  uint32 speed = 4;                 //鱼速度
  Point point = 5;                  //坐标
  uint32 line = 6;                  //鱼群路线
  uint32 form = 7;                  //鱼群形状  魔法王者恒定为0 捕鱼视情况而定
  uint32 special = 8;               //0 普通鱼 1 特殊鱼 2 Boss
  uint32 times = 9;                 //鱼倍数
  float fish_size = 10;             //鱼大小
}

message SkillInfo {
  uint32 id = 1;                    //技能id 0无 1全屏击杀 2冰冻 3狂暴怪
  float time_out = 2;               //持续时间
  uint32 seat_id = 3;               //影响的座位id 只针对狂暴怪有效
}

//鱼死亡通知
message GameFishDiedNotify {
  uint32 player_id = 1;             //击杀鱼的玩家
  repeated uint32 fish_id = 2;      //鱼id
  string score = 3;                 //获得的金币
  string gold = 4;                  //当前金币
  uint32 seat_id = 5;               //击杀鱼的玩家
  SkillInfo skill_info = 6;         //击杀鱼后的技能效果
}

message GameReconnectInfo{
  RoomInfo room_info = 1;                     //房间信息
  PlayerInfo player_info = 2;                 //玩家信息
  repeated PlayerInfo other_player_info = 3;  //其他玩家坐标
  repeated GameBulletCreateRes bullets = 4;   //子弹
  repeated GameFishFreshNotify fishs = 5;     //鱼
  repeated uint32 bullet_level = 6;           //子弹等级数组
  repeated SkillInfo skill_info = 7;          //当前正在执行的技能效果
  repeated MonsterTimes monster_times = 8;          //怪物倍数列表
}

message MonsterTimes {
  uint32 fish_type = 1;             //鱼的类型
  float times = 2;                 //倍数
}

message GameOtherJoin {
  PlayerInfo player_info = 1; //当前玩家
}

message GameOtherLeave {
  PlayerInfo player_info = 1; //当前玩家
}
