syntax = "proto3";
package QZNN;

message MsgBody  {

    GameStartNotify   game_start_notify = 1;   

    GrabBankReq       grab_bank_req = 2;
    GrabBankRes       grab_bank_res = 3; 

    BetReq            bet_req = 4;
    BetRes            bet_res = 5;

    GameConfirmBankNotify  game_confirm_bank_notify = 12;

    StartBetNotify    start_bet_notify = 6;

    DealCardNotify    deal_card_notify = 7;

    CardResultReq     card_result_req = 8;
    CardResultRes     card_result_res = 9;

    GameConcludeNotify  game_conclude_notify = 10;

    GameReconnectInfo game_reconnect_info = 11;

}



message RoomInfo  {
    string room_base_note  = 1 ;      //房间底注
    string room_code = 2;             //房间编号信息
}



message PlayerInfo  {
    uint32 player_id = 1;
    uint32 seat_id = 2;         //座位ID
    string username = 3;
    string gold = 4;
    uint32 headimg_id = 5;
    uint32 sex = 6;            //性别
    uint32 vip_level = 7;           //vip等级
}


message GameStartNotify  {      //游戏开始通知
    repeated PlayerInfo player_list = 1;           //所有玩家
    repeated uint32 grab_multiple_arr = 2;         //抢庄倍数
    RoomInfo room_info = 3;
    uint32 time_out = 4;                           //抢庄的倒计时(单位秒)
}


message GrabBankReq  {         //抢庄请求
    uint32 player_id = 1;
    uint32 grab_multiple = 2;
}

message GrabBankRes  {        
    uint32 seat_id = 1;
    uint32 grab_multiple = 2;  
}

//确定庄家通知 只播动画
message GameConfirmBankNotify {
    uint32 bank_seat_id = 1;                      //最终庄家的id
    repeated uint32 higher_grab_multiple_arr = 2; //抢到了最高倍数的id数组
}

message StartBetNotify {      //通知开始下注 定庄完成
    uint32 bank_seat_id = 1;                                 //最终庄家的id
    repeated uint32 bet_multiple_arr = 2;                    //该玩家能下倍数的数组
    uint32 bank_grab_multiple = 3;                           //庄家抢的倍数
    uint32 time_out = 4;                                     //下注的倒计时(单位秒)
}

message BetReq  {
    uint32 player_id = 1;
    uint32 bet_multiple = 2;
}

message BetRes  {
    uint32 seat_id = 1;
    uint32 bet_multiple = 2;  
}


message DealCardNotify {    //下注完成 发牌
    repeated uint32 card_arr = 1;
    uint32 val_type = 2;                  //玩家的牌型, 0~10无牛到牛牛,14,15四五花牛24,25炸弹牛,五小牛
    uint32 time_out = 3;                  //摊牌的倒计时(单位秒)
}

message CardResult  {
    uint32 seat_id = 1;
    repeated uint32 card_arr = 2;             //每个玩家的5张牌
    uint32 val_type = 3;                      //玩家的牌型, 0~10无牛到牛牛,14,15四五花牛24,25炸弹牛,五小牛
    uint32 val_multiple = 4;                  //牌对应的倍数
}

message CardResultReq  {        //开牌请求
    uint32 player_id = 1;
} 

message CardResultRes  {        //开牌结果
    CardResult card_result = 1;
}


message GameConclude {
    uint32 seat_id = 1;     //玩家座位号
    string gold = 2;         //玩家结算后的金币数量
    string score = 3;        //输赢的分数
}

message GameConcludeNotify  {        //结算推送
    repeated GameConclude game_conclude = 1;
    uint32 bank_seat_id = 2;         //庄家位置
}


message GameReconnectInfo  {
    uint32 game_state = 1;                                      //游戏状态1为抢庄，2为下注，3为开牌，4为结算

    GameStartNotify game_start_notify = 2;                      //游戏开始的信息，包括所有玩家的信息，抢庄的剩余时间等
    
    repeated GrabBankRes grab_bank_res = 3;                     //所有抢庄的信息

    StartBetNotify start_bet_notify = 4;                        //通知下注

    repeated BetRes bet_res = 5;                                //所有下注的信息

    DealCardNotify  deal_card_notify = 6;                       //通知开牌

    repeated CardResultRes card_result_res = 7;                 //所有开牌的信息

    GameConcludeNotify game_conclude_notify = 8;                //所有结算的信息
}
