syntax = "proto3";
package Chat;


message MsgBody {
    SendChatMsgReq send_chatmsg_req = 1; //发送聊天消息请求
    SendChatMsgRes send_chatmsg_res = 2;
    GetChatMsgRecordReq get_chatmsg_record_req = 3; //获取上下文聊天记录,用于初次进入房间或者断线重连用
    GetChatMsgRecordRes get_chatmsg_record_res = 4;
    ChatMsgNotify chat_msg_notify = 5; //聊天消息推送
}

message RoomMsgInfo {
    uint32 game_id = 1; //要加入游戏id
    uint32 room_level = 2; //要加入房间等级
    string friend_room_id = 3; //好友房id如果在游戏中的话
    uint32 need_carry = 4;
    uint32 room_min_entrance = 5;
    string game_des = 6; //游戏描述 xx游戏xx场
}

message UserInfo {
    uint32 user_id = 1;
    uint32 vip_level = 2; //vip等级
    uint32 headimg_id = 3; //头像id
    string nick_name = 4; //昵称
}

message ChatContent {
    uint32 send_id = 1; //发送方 玩家1 系统2
    uint32 place_id = 2; //大厅1 游戏2
    UserInfo user_info = 3; //玩家信息 如果有的话
    uint32 content_type = 4; //文字内容1 表情2 加入房间3
    uint32 content_time = 5; //消息发送时间 不需要客户端填
    string raw_content = 6; //消息内容
    uint32 msg_index = 7; //排序序号 如果是1 则表示置顶
    RoomMsgInfo room_msg_info = 8; //包含房间的信息
    uint32 club_id = 9;             //俱乐部id
}

message SendChatMsgReq {
    uint32 user_id = 1; //玩家id
    uint32 place_id = 2; //大厅1 游戏2
    uint32 content_type = 3; //文字内容1 表情2 加入房间3
    uint32 content_sub_type = 4; //子子类型 如果是加入房间消息 邀请分享1 分享大厅2
    string raw_content = 5; //消息内容
    uint32 game_id = 6; //游戏id
    uint32 room_level = 7; //房间等级
    string friend_room_id = 8; //好友房code如果在好友房的话
    uint32 club_id = 9;             //俱乐部id
}

message SendChatMsgRes {
    uint32 ok = 1; //1成功 2失败
    string show_tips = 2; //分享时成功或失败的提示
}

message GetChatMsgRecordReq {
    uint32 user_id = 1;
    uint32 place_id = 2; //大厅1 游戏2
    uint32 game_id = 3; //游戏id
    uint32 room_level = 4; //房间等级
    string friend_room_id = 5; //好友房code如果在好友房的话
    uint32 club_id = 6;             //俱乐部id
}

message GetChatMsgRecordRes {
    repeated ChatContent chat_content_list = 1;
    uint32 place_front_time = 2; //置顶时间
    uint32 less_speak_vip_level = 3; //最低发言vip等级>=
    repeated string quick_msg_list = 4; //快捷语列表
    uint32 chat_switch = 5; // 0关闭 1打开
}

message ChatMsgNotify {
    ChatContent chat_content = 1; //聊天内容
}
