syntax = "proto3";
package TZW;

message MsgBody  {
    GameStartNotify   game_start_notify         = 1;   //游戏开始（下注）
    RandomDiceNotify  random_dice_notify        = 2;   //摇骰子通知
    StartBetNotify    start_bet_notify          = 3;   //下注通知
    BetReq            bet_req = 4;
    BetNotify         bet_notify = 5;
    OtherBetNotify    other_bet_notify = 6;

    ShowDiceNotify    show_dice_notify = 7;     //开骰子通知

    GameConcludeNotify  game_conclude_notify = 8; //结算

    GameReconnectInfo game_reconnect_info = 9; //加入房间

    OtherUsersReq     other_users_req = 10;
    OtherUsersRes     other_users_res = 11;

    WaitNextNotify    wait_next_notify = 12;    //等待下一局

    ContinueBetReq  continue_bet_req = 13;      //续投请求
    ContinueBetRes  continue_bet_res = 14;      //续投回应
}

message PlayerInfo  {
    uint32 player_id = 1;
    uint32 seat_id = 2;         //座位ID
    string username = 3;
    string  gold = 4;
    uint32 headimg_id = 5;
    uint32 sex = 6;      //性别
}

//房间信息
message RoomInfo  {
    string room_code = 1;             //房间编号信息
}

//游戏开始通知 下注
message GameStartNotify  {
    RoomInfo room_info = 1;           //房间信息
    uint32 enable_continue = 2;       //是否可以续投 0不可 1可以
    uint32 time_out = 3;              //下注时间
}

//洗牌通知
message RandomDiceNotify {
    uint32 time_out = 1;                //洗牌时间
}

//开始下注通知
message StartBetNotify {
    uint32 time_out = 1;                //洗牌时间
}

//下注请求
message BetReq   {
    uint32 player_id = 1;
    uint32 bet_value = 2;         //下注值
    uint32 loc = 3;			      //下注的位置(1~6 豹子1~6 7小8中9大10庄)
}

//下注筹码值情况
message BetValueInfo {
    uint32 loc = 1;             //每个筹码的位置(1~6 豹子1~6 7小8中9大10庄)
    repeated uint32 bet_value = 2;       //下注的筹码值
}

//已下注筹码数量信息
message BetNumInfo {
    uint32 user_bet_num = 1;        //我已下注数量
    uint32 sum_bet_num = 2;         //所有玩家已下注数量
}

//下注通知  此通知只会发给自己
message BetNotify  {
    uint32 result = 1 ;                         // 下注结果: 1=成功,2=限红 3金币不足 4非下注状态
    string  gold = 2;                           //更新玩家金币
    BetValueInfo bet_value_info = 3;            // 下注信息
    repeated BetNumInfo bet_num_info = 4;       // 所有玩家在各个位置下注总量(天地玄黄)
}

//其他玩家下注通知 每隔一段时间一起发过来
message OtherBetNotify {
   repeated BetValueInfo bet_value_info = 1;    // 筹码应该丢在那个位置
   repeated BetNumInfo bet_num_info = 2;        // 所有玩家在各个位置下注总量(天地玄黄)
}

//中奖区域
message CardMsgInfo {
    uint32 loc = 1 ;                     //1~6 豹子1~6 7小8中9大10庄
    string score = 2;                    //该区域得分情况
}

//一局开奖数据
message OneJuResult{
    repeated uint32 open_nums = 1;                      //开骰数据[6,6]
    repeated CardMsgInfo card_msg_info = 2;             //一局所有开奖点数组      
}

//开骰子通知
message ShowDiceNotify {
    repeated History res = 1;                  //历史记录
    //repeated uint32 open_nums = 2;          //开骰数据[6,6]
    uint32 time_out = 2;                    //开牌的倒计时(单位秒)
}

//结算通知
message GameConcludeNotify  {
    repeated CardMsgInfo card_msg_info = 1;     //中奖区域
    string gold = 2;                            //玩家结算后的金币数量
    string score = 3;                           //输赢的分数
    uint32 time_out = 4;                        //结束时间
    bool is_bet_req = 5;                        //是否有下注，true = 有下注,false = 没下注
    repeated PlayerInfo player_list = 6;        //前5名的玩家信息
}

//休息通知
message WaitNextNotify {
    uint32 time_out = 1;
}

//重连信息
message GameReconnectInfo  {
    string gold = 1;                                    //玩家剩余金币
    string score = 2;                                   //输赢的分数
    RoomInfo room_info = 3;                             //房间信息
    uint32 game_state = 4;                              //游戏状态  1=下注中 2=开骰中 3=摇骰中 4=结算 5=休息
    uint32 time_out = 5;                                //当前阶段剩下的时间(单位秒)
    repeated BetNumInfo bet_num_info = 6;               //下注数据
    repeated BetValueInfo bet_value_info = 7;           //桌面筹码值 只保存最后 15 个下注筹码值
    OneJuResult new_open_arry = 8;                      //如果已经到了开奖阶段，里面有开奖数据
    uint32 enable_continue = 9;                         //是否可以续投 0不可 1可以
    repeated uint32 limit_num = 10;                     //天地玄黄限红数组
    repeated History history_nums = 11;                  //历史记录
    repeated PlayerInfo player_list = 12;                //如果结算阶段 有前5名的玩家信息
    repeated uint32 bet_conis_value = 13;               //下注筹码数组
    bool is_bet_req = 14;                               //是否有下注，true = 有下注,false = 没下注
    repeated uint32 game_multiple_arr = 15;             //倍数数组1~6 豹子1~6 7小8中9大
}

//历史记录
message History {
    uint32 result_num = 1;                              //小 0 和 1 大 2
    repeated uint32 open_nums = 2;                     //开骰数据[6,6]
}

//其它玩家列表
message OtherUsersReq {
    uint32 player_id = 1;
}

message OtherUsersRes {
    repeated PlayerInfo player_list = 1;      //20名的玩家信息
    uint32 totalplayers = 2;                  //房间总玩家数量
}

//续投请求
message ContinueBetReq {
    uint32 player_id = 1;
}

//续投回应
message ContinueBetRes {
    uint32 result = 1; //0=续投失败 1=成功 2金币不足
}
