syntax = "proto3";
package Account;



message MsgBody  {
    ErrorRes            err_res = 1;
    AuthenReq           authen_req = 2;
    AuthenRes           authen_res = 3;
    RegisterAccount     register_account_req = 4;
    RegisterAccount     register_account_res = 5;
    LoginReq            login_req = 6;
    LoginRes            login_res = 7;

    AccoutSafetyReq     accout_safety_req = 8;
    AccoutSafetyRes     accout_safety_res = 9;

    EditDataReq        edit_data_req = 12;   //编译资料
    EditDataRes        edit_data_res = 13;   
    
    SetLoginPasswordReq   set_login_password_req = 14;   //设置登录密码
    SetLoginPasswordRes   set_login_password_res = 15;

    ModifyLoginPasswordReq    modify_login_password_req = 16;   //修改登录密码
    ModifyLoginPasswordRes    modify_login_password_res = 17;

    SetSecurePasswordReq     set_secure_password_req = 18;   //设置安全密码
    SetSecurePasswordRes     set_secure_password_res = 19;

    ModifySecureReq         modify_secure_req = 20;     //修改安全密码
    ModifySecureRes         modify_secure_res = 21;     
    
    BindMobilePhoneReq      bind_mobile_phone_req = 22;   //绑定手机
    BindMobilePhoneRes      bind_mobile_phone_res = 23;

    CheckBoundPhoneVerCodeReq    check_bound_phone_vercode_req = 24;   //修改绑定手机之前检验原手机
    CheckBoundPhoneVerCodeRes    check_bound_phone_vercode_res = 25;
    ModifyBindMobilePhoneReq     modify_bind_mobile_phone_req = 26;     //修改绑定手机
    ModifyBindMobilePhoneRes     modify_bind_mobile_phone_res = 27;

    GetUserInfoReq      get_userinfo_req = 28;
    GetUserInfoRes      get_userinfo_res = 29;

    AddUserGoldReq add_user_gold_req = 30;

    RetrievePasswordReq    retrieve_password_req   = 31;              //找回密码
    RetrievePasswordRes    retrieve_password_res   = 32;              

    RetrieveSafeCodeReq    retrieve_safecode_req   = 33;              //找回安全码
    RetrieveSafeCodeRes    retrieve_safecode_res   = 34; 

    KickoutNotify  kickout_notify = 41;   //重复登录 踢出通知

    UserDataChangeNotify  userdata_change_notify = 42;            //玩家数据改变通知
    UserGoldChangeNotify  usergold_change_notify = 43; //专门针对玩家金币的

    LoginReq account_reg_req = 44;
    LoginRes account_reg_res = 45;

    UserVipChangeNotify uservip_change_notify = 50; //vip等级变化提示推送
}

message ErrorRes {
    int32 err_no = 1;           //错误原因
    int32 err_subno = 2;        //错误子原因
    string err_msg = 3;         //错误描述
}

message AuthenReq  {
    string secret = 1;
}

message AuthenRes  {
    string res = 1;
}

message RegisterAccount  {
    uint32 userid = 1;
    string account = 2;
    string password = 3;
    uint32 sex = 4;
    string openid = 5;
    string headimgurl = 6;
    string nickname = 7;
    string unionid = 8;
    string token = 9;
    string ver_code = 10;
}


message LoginReq  {
    string account = 1;
    string password = 2;
    string ver_code = 3;       //验证码登陆
    uint32 login_type = 4;     //1 账号登陆, 2 游客, 3验证码 4 token 5 账号
    string model = 5; //机型
    string channel = 6; //渠道
    string token = 7; //有token可以没有其他1-4字段
    uint32 parent_id = 8;      //上级代理id
    string login_addr = 9;                  //登录地址  （城市）
    string longitude_latitude = 10;         // 经、纬度
    string deviceID = 11;                   // 设备ID
    string login_ip = 12;      //ip地址
}

message UserInfo  {
    uint32 userid = 1;
    string nickname = 2;            //玩家姓名
    string platform_userid = 3;     //平台ID
    uint32 login_type = 4;          //1 账号登陆, 2 游客, 3验证码 4 token 5 账号
    uint32 sex = 5;                 // 1?0(1男 0女)
    string title = 6;               //头衔
    string gold = 7;                //金币数量
    uint32 vip_score = 8;           //vip积分
    uint32 vip_level = 9;           //vip等级
    string headimgurl = 10;         //头像
    string phone_number = 11;       //手机号
    uint32 parent_id = 12;          //上级代理id(存在上级id就隐藏绑定窗口)
    string exchange_code = 13;      //兑换码（若存在则已经兑换）
    uint32 bind_reward_status = 14; //是否已领取绑定奖励 0未领取 1已领取
    string limit_info = 15; //vip功能限制 
    repeated Querytype query_types = 16; //个人信息其他记录查询类型
}

message Querytype {
    uint32 id = 1; //后台类型ID
    string name = 2; //该类型名称
}

message JoinedGameInfo  {
    bool has = 1;                      //是否加入过房间
    uint32 game_type = 2;
    uint32 room_level = 3;
    uint32 room_id = 4;                //好友房才有这个字段
    string ky_game_url = 5;            //开元棋牌才有这个字段
    uint32 club_id = 6;  
}

message AccoutSafetyInfo  {
    bool has_password = 1;
    bool has_safe_password = 2;
    bool has_bind_phone = 3;
    uint32 safe_level = 4;
    uint32 last_login_time = 5;
    string last_login_place = 6;
}

message LoginRes  {
    uint32 userid = 1;
    string token = 2;
    UserInfo user_info = 3;
    AccoutSafetyInfo safety_info = 4;
    JoinedGameInfo joined_game_info = 5;
}

message AccoutSafetyReq  {    //账号安全请求
    uint32 userid = 1;
}

message AccoutSafetyRes  {
    uint32 userid = 1;
    bool has_password = 2;
    bool has_safe_password = 3;
    bool has_bind_phone = 4;
    uint32 safe_level = 5;
    uint32 last_login_time = 6;
    string last_login_place = 7;
}


message EditDataReq {    //编辑资料
    uint32 userid = 1;
    uint32 imgid = 2;
    string nickname = 3;
    uint32 type = 4;    //1改名字 2改头像
}

message EditDataRes {
    uint32 userid = 1;
    string nickname = 2;
    uint32 headimgurl = 3;
}

message SetLoginPasswordReq {    //设置登录密码
    uint32 userid = 1;
    string password = 2;
}

message SetLoginPasswordRes {
    bool ok = 1;
}

message ModifyLoginPasswordReq {    //修改登录密码
    uint32 userid = 1;
    string oldpassword = 2;
    string newpassword = 3;
}

message ModifyLoginPasswordRes {
    bool ok = 1;
}

message SetSecurePasswordReq {   //设置安全密码
    uint32 userid = 1;
    string password = 2;
}

message SetSecurePasswordRes {
   bool ok = 1;
}

message ModifySecureReq {   //修改安全码
    uint32 userid = 1;
    string oldsecure = 2;
    string newsecure = 3;
}

message ModifySecureRes {
    bool ok = 1;
}

message BindMobilePhoneReq {   //绑定手机
    uint32 userid = 1;
    string phonenumber = 2;
    string identifycode = 3;
    string channel = 4;
    uint32 parent_id = 5;      //上级代理id
    string login_addr = 6;     //登录地址
    string longitude_latitude = 7;         // 经、纬度
    string deviceID = 8;                   // 设备ID
    string model = 9;          //机型
}

message BindMobilePhoneRes {
    bool ok = 1;
    string msg = 2;      //是绑定还是登陆
    LoginRes login_res = 3;
}

message CheckBoundPhoneVerCodeReq    {     //修改绑定手机钱检验原手机
    uint32 userid = 1;
    string phonenumber = 2;
    string identifycode = 3;
    string channel = 4;
}

message CheckBoundPhoneVerCodeRes {
    bool ok = 1;
}

message  ModifyBindMobilePhoneReq {       //修改绑定手机
    uint32 userid = 1;
    string newphonenumber = 2;
    string identifycode = 3;
    string channel = 4;
}  

message ModifyBindMobilePhoneRes {
    bool ok = 1;
    string phonenumber = 2;
}


message GetUserInfoReq  {
    uint32 userid = 1;
}


message GetUserInfoRes  {
    UserInfo user_info = 1;
}


message VipLevelInfo  {
    string vip_level = 1;
    string vip_des = 2;
    uint32 need_score = 3;
    string desc = 4;
}

message AddUserGoldReq {
    uint32 userid = 1;
    string gold = 2; //添加的金币
}

message RetrievePasswordReq  {
    string account = 1;             //手机号
    string channel = 2;             //渠道号
    string identifycode = 3;        //验证码
    string password = 4;            //密码
    uint32 userid = 5;
}

message RetrievePasswordRes  {
    bool ok = 1;
}

message RetrieveSafeCodeReq  {
    string account = 1;             //手机号
    string channel = 2;             //渠道号
    string identifycode = 3;        //验证码
    string password = 4;            //安全码
    uint32 userid = 5;
}

message RetrieveSafeCodeRes  {
    bool ok = 1;
}

message KickoutNotify  {      //被踢出房间通知
    uint32 code = 1;          //被踢出的原因 
    string des = 2;           //文字说明
}

message UserDataChangeNotify  {
    uint32 userid = 1;    //玩家id  其实用不到
}

message UserGoldChangeNotify {
    uint32 userid = 1;  //玩家id
    string gold = 2;    //最新金币
    string msg = 3;     //更新说明
}

message UserVipChangeNotify {
    uint32 userid = 1; //玩家id
    uint32 new_vip_level = 2; //新等级
    uint32 old_vip_level = 3; //旧等级
}
