syntax = "proto3";
package TB;

message MsgBody  {
    GameStartNotify     game_start_notify = 1;          //游戏开始
    StartBetNotify      start_bet_notify = 2;           //开始下注通知
    OpenNotify          open_notify = 3;                //开骰通知
    GameConcludeNotify  game_conclude_notify = 4;       //结算通知
    WaitNotify          wait_notify = 5;                //休息等待通知
    BetReq              bet_req = 6;                    //下注请求
    BetRes              bet_res = 7;                    //下注回应
    OtherBetNotify      other_bet_notify = 8;           //其他玩家下注通知
    GameReconnectInfo   game_reconnect_info = 9;        //重连
    PlayerListReq       player_list_req = 10;           //玩家列表请求
    PlayerListRes       player_list_res = 11;           //玩家列表回应
}

//房间信息
message RoomInfo  {
    string room_code = 1;                               //房间编号信息
}

//游戏开始通知
message GameStartNotify  {            
    RoomInfo room_info = 1;                             //房间信息
    uint32 time_out = 2;                                //开始状态时间  
    repeated BetInfo player_last_bet = 3;               //玩家最后一次下注数据
}

//通知开始下注
message StartBetNotify {      
    uint32 time_out = 1;                                //下注状态时间(单位秒)
}

//开骰通知
message OpenNotify {                    
    repeated uint32 open_nums = 1;                      //开骰数据[6,6,6]
    uint32 time_out = 2;                                //开奖状态时间(单位秒)
}

//中奖数据
message WinInfo {
    uint32 loc = 1;                                     //坐标（押注图1~52个下注区域ID）
    uint32 betValue = 2;                                //坐标对应倍数
    string my_win_Coins = 3;                            //我的中奖金额
}

//结算通知
message GameConcludeNotify  { 
    string gold = 1;                                    //玩家结算后的金币数量
    string score = 2;                                   //输赢的分数
    uint32 time_out = 3;                                //结束状态时间
    repeated uint32 win_loc = 4;                        //开奖坐标数组
    repeated WinInfo resultInfo = 5;                    //每个玩家中奖具体数据
	repeated uint32 history_nums = 6;                  //统计次数数组， 依次为 小大单双全骰
}

//休息等待通知
message WaitNotify {                    
    uint32 time_out = 1;                                //状态时间(单位秒)
}

//下注请求
message BetReq {
    uint32 player_id = 1;                               //玩家ID
    uint32 bet_value = 2;                               //下注值
    uint32 loc = 3;			                            //下注的位置
}

//坐标对应的数据集合
message BetInfo {
    uint32 loc = 1;                                     //坐标（押注图1~52个下注区域ID）
    uint32 betValue = 2;                                //坐标对应倍数
    string my_bet_Coins = 3;                            //坐标"我"的下注筹码量（其他玩家N秒推送时每次更新时也在这取值）
    string total_bet_coins = 4;                         //坐标下注总量
}

//我的下注通知 
message BetRes  {
    uint32 result = 1 ;                                 //下注结果: 1=成功,2=达到限红, 3=金币不足
    string gold = 2;                                    //更新玩家金币
    string betConis = 3;                                //下注金额
    BetInfo betInfo = 4;                                //该坐标区域下注量信息
}

//其他玩家下注通知 每隔N秒一起发过来
message OtherBetNotify {
   repeated BetInfo bet_info = 1;                       //本次推送下注信息的坐标数据数组
}

//一个开奖点数据
message OnePosResult {
    uint32 loc = 1;                                     //开奖坐标位置ID
    uint32 betValue = 2;                                //坐标对应倍数
}

//一局开奖数据
message OneJuResult{
    repeated uint32 open_nums = 1;                      //开骰数据[6,6,6]
    repeated OnePosResult oneju_result = 2;             //一局所有开奖点数组      
}

//重连信息
message GameReconnectInfo  {
    string gold = 1;                                    //玩家剩余金币
    string score = 2;                                   //输赢的分数
    RoomInfo room_info = 3;                             //房间信息
    uint32 game_state = 4;                              //游戏状态 1开始2押注3开骰4结算5休息
    uint32 time_out = 5;                                //当前阶段剩下的时间(单位秒)
    repeated BetInfo oneJuBet = 6;                      //下注数据
    OneJuResult new_open_arry = 7;                      //如果已经到了开奖阶段，里面有开奖数据
    OneJuResult last_open_arry = 8;                     //上局开奖坐标
    repeated OneJuResult history = 9;                   //开奖记录
    repeated uint32 litmitNumber = 10;                  //限红
    repeated uint32 bet_conis_value = 11;               //下注筹码数组
    repeated uint32 history_nums = 12;                  //统计次数数组， 依次为 小大单双全骰
    repeated BetInfo player_last_bet = 13;              //玩家最后一次下注数据 
}

//玩家数据
message PlayerInfo  {
    uint32 player_id = 1;                  
    string username = 2;
    string gold = 3;
    uint32 headimg_id = 4;
    uint32 sex = 5;                                 
}

//玩家列表
message PlayerListReq {
    uint32 player_id = 1;
}

message PlayerListRes {
    repeated PlayerInfo player_list = 1;                //20名的玩家信息
    uint32 totalplayers = 2;                            //房间总玩家数量
}
