syntax = "proto3";
package QHB;

message MsgBody  {
    GameStartNotify     game_start_notify = 1;          //游戏开始

    SendBounsNotify     send_bouns_notify = 2;          //发红包

    RobBounsReq         rob_bouns_req = 3;              //抢红包请求
    RobBounsRes         rob_bouns_res = 4;              //抢红包回应
    RobBounsNotify      rob_bouns_notify = 5;           //有玩家抢到红包的通知 

    GameConcludeNotify  game_conclude_notify = 6;       //结算

    GameReconnectInfo   game_reconnect_info = 7;        //加入房间

    SetupBounsReq       setup_bouns_req = 8;            //玩家设置发送红包信息(上庄)请求
    SetupBounsRes       setup_bouns_res = 9;            //玩家设置发送红包回应

    ConfirmBounsReq     confirm_bouns_req = 10;         //玩家确认设置上下庄信息请求
    ConfirmBounsRes     confirm_bouns_res = 11;         //玩家确认设置上下庄信息请求

    PlayerListReq       player_list_req = 12;           //玩家列表请求
    PlayerListRes       player_list_res = 13;           //玩家列表回应

    XufaReq             xu_fa_req = 14;                 //续发请求

    QhbLeaveRoomReq     qhb_leave_room = 15;            //抢红包确定退房
    QhbLeaveRoomNotify  qhb_leave_notify = 16;          //抢红包弹框提示
    QhbLeaveRoomConfirm qhb_leave_confirm = 17;         //抢红包确定退房
}

//房间信息
message RoomInfo  {
    string room_code = 1;                               //房间编号信息
    uint32 nums = 2;                                    //几包房
    string peilv = 3;                                   //赔率 
}

//游戏开始通知
message GameStartNotify  {            
    RoomInfo room_info = 1;                             //房间信息
    uint32 time_out = 2;                                //开始状态时间
    BounsInfo xufa_info = 3;                            //续发数据
   
}

//发出的红包信息
message BounsInfo{
    uint32 nums = 1;                                    //还剩多少个
    string totalMoney = 2;                              //红包金额
    uint32 mineNumber = 3;                              //雷号
    uint32 player_id = 4;                               //发红包玩家ID
    uint32 headimg_id = 5;                              //发红包的头像ID  
    uint32 bounsNums = 6;                               //连续发包次数     
	string username = 7;                                //名字	
}

//抢到的红包信息
message RobbounsInfo{
    uint32 player_id = 1;                               //玩家ID
    uint32 headimg_id = 2;                              //抢到红包玩家的头像ID
    string bounsMoney = 3;                              //本次抢到的红包金额
    uint32 isMine = 4;                                  //是否中雷 0不中 1雷包
    string score = 5;                                   //最终得分(税后输赢)
    uint32 isBankuser = 6;                              //是否为庄家
    uint32 islucky = 7;                                 //是否幸运星（本局赢取最多）
    string username = 8;            
}

//发红包
message SendBounsNotify {      
    BounsInfo bouns_info = 1;                           //红包信息
    uint32 time_out = 2;                                //下注状态时间(单位秒)
}

//抢红包请求
message RobBounsReq   {
    uint32 player_id = 1;                               //玩家ID
}

//自己抢红包回应
message RobBounsRes   {
    uint32 playerId = 1;                                //玩家ID
    uint32 result = 2;                                  //抢红包结果 0成功 1手慢啦 2金币不足
    uint32 isMine = 3;                                  //是否中雷 0不中 1雷包
    uint32 specEvent = 4;                               //特殊事件 [最小，反顺，顺子，豹子]
    string bounsMoney = 5;                              //本次抢到的红包金额
    repeated uint32 specCards = 6;                      //特殊红包个数[最小，反顺，顺子，豹子]
}

//有玩家抢到红包的通知
message RobBounsNotify {
    uint32 player_id = 1;                               //玩家ID
    uint32 headimg_id = 2;                              //玩家头像
    uint32 leftNum = 3;                                 //剩余红包个数
    string username = 4;        
}

//结算通知
message GameConcludeNotify  { 
    string score = 1;                                   //输赢的分数
    string gold = 2;                                    //玩家结算后的金币数量
    string bankScore = 3;                               //庄家得分
    uint32 mineNumber = 4;                              //雷号
    uint32 time_out = 5;                                //剩余时间
    repeated RobbounsInfo rob_bouns_info = 6;           //抢到红包的信息列表
    repeated uint32 specCards = 7;                      //特殊红包个数[最小，反顺，顺子，豹子]
    string poolGold = 8;                                //奖池金额
    uint32 headimg_id = 9;                              //发红包的头像ID 
    uint32 specEvent = 10;                              //特殊事件 [最小，反顺，顺子，豹子]
    uint32 isCaishen = 11;                              //是否中财神 0没有，1中
    repeated uint32 caishenSpecCards = 12;              //财神后特殊红包个数[最小，反顺，顺子，豹子]
    string caishen_score = 13;                          //财神中的钱    
    string caishen_gold = 14;                           //财神后的玩家金币
}

//重连信息
message GameReconnectInfo  {
    RoomInfo room_info = 1;                             //房间信息
    uint32 game_state  = 2;                             //游戏状态 1开始2发红包阶段3结算阶段
    uint32 time_out    = 3;                             //当前阶段剩下的时间(单位秒)
    BounsInfo bouns_info = 4;                           //发出来的红包信息
    repeated RobbounsInfo rob_bouns_info = 5;           //抢到红包的信息列表
    string score = 6;                                   //玩家得分
    string gold = 7;                                    //玩家剩余金币
    string bankScore = 8;                               //庄家得分
    repeated uint32 specCards = 9;                      //特殊红包个数[最小，反顺，顺子，豹子]
    string poolGold = 10;                               //奖池金额
    uint32 headimg_id = 11;                             //发红包的头像ID 
    BounsInfo xufa_info = 12;                           //续发数据
    uint32 hasRob = 13;                                 //玩家本轮是否已经抢过红包了 1抢过了 0没抢过
}

//设置红包请求
message SetupBounsReq {
    uint32 player_id = 1;                               //发红包玩家信息
}

//设置红包回应
message SetupBounsRes {
    BounsInfo bouns_info = 1;                           //玩家之前设置的红包信息，没有就是没设置
    repeated BounsInfo bank_list = 2;                   //庄家列表
    string maxValue = 3;                                //配置最大值
}

//上下庄确定请求
message ConfirmBounsReq {
    uint32 code = 1;                                    //1确认设置发红包信息（上庄）   2确认取消发红包
    BounsInfo bouns_info = 2;                           //发送红包信息
}

//上下庄确认回应
message ConfirmBounsRes {
    uint32 result = 1;                                  //0设置发送或取消红包成功 1设置发送红包失败（金币不足）2取消失败（玩家不在列表）
    string score = 2;                                   //正为取消上庄退还，负为上庄请求扣除
    string gold = 3;                                    //玩家金币
}

message PlayerInfo  {
    uint32 player_id = 1;                  
    string username = 2;
    string gold = 3;
    uint32 headimg_id = 4;
    uint32 sex = 5;                                 
}

//玩家列表
message PlayerListReq {
    uint32 player_id = 1;
}

message PlayerListRes {
    repeated PlayerInfo player_list = 1;                //20名的玩家信息
    uint32 totalplayers = 2;                            //房间总玩家数量
}

message XufaReq {
    uint32 player_id = 1;  
}

message QhbLeaveRoomReq {
    uint32 player_id = 1;                               //抢红包退房走这条协议
}

message QhbLeaveRoomNotify {
    uint32 code = 1;                                    //0正常退出  1游戏中  2下庄
}

message QhbLeaveRoomConfirm {
    uint32 player_id = 1;                              //抢红包确定退房
}
