syntax = "proto3";
package LHD;

message MsgBody  {
    GameStartNotify     game_start_notify       = 1;    //游戏开始（发牌）

    GameBetNotify       game_bet_notify         = 2;    //通知开始下注

    BetReq              bet_req                 = 3;
    BetNotify           bet_notify              = 4;
    OtherBetNotify      other_bet_notify        = 5;

    ShowCardNotify      show_card_notify        = 6;    //开牌通知

    GameConcludeNotify  game_conclude_notify    = 7;    //结算通知

    GameReconnectInfo   game_reconnect_info     = 8;    //加入房间

    OtherUsersReq       other_users_req         = 9;
    OtherUsersRes       other_users_res         = 10;
}

message PlayerInfo  {
    uint32 player_id = 1;
    uint32 seat_id = 2;         //座位ID
    string username = 3;
    string  gold = 4;
    uint32 headimg_id = 5;
    uint32 sex = 6;      //性别
}

//房间信息
message RoomInfo  {
    string room_code = 1;             //房间编号信息
}

//游戏开始通知 发牌中
message GameStartNotify  {
    RoomInfo room_info = 1;           //房间信息
    uint32 time_out = 2;              //发牌时间 2秒
}

//通知开始下注
message GameBetNotify {
    uint32 time_out = 1;              //下注时间
}

//下注请求
message BetReq   {
    uint32 player_id = 1;
    uint32 bet_value = 2;         //下注值
    uint32 loc = 3;			      //下注的位置(1龙 2虎 3和)
}

//下注筹码值情况
message BetValueInfo {
    uint32 loc = 1;                     //每个筹码的位置(1龙 2虎 3和)
    repeated uint32 bet_value = 2;      //下注的筹码值
}

//已下注筹码数量信息
message BetNumInfo {
    uint32 user_bet_num = 1;        //我已下注数量
    uint32 sum_bet_num = 2;         //所有玩家已下注数量
}

//下注通知  此通知只会发给自己
message BetNotify  {
    uint32 result = 1 ;                         // 下注结果: 1=成功,2=限红 3金币不足 4非下注状态
    string  gold = 2;                           // 更新玩家金币
    BetValueInfo bet_value_info = 3;            // 下注信息
    repeated BetNumInfo bet_num_info = 4;       // 所有玩家在各个位置下注总量(龙虎和)
}

//其他玩家下注通知 每隔一段时间一起发过来
message OtherBetNotify {
   repeated BetValueInfo bet_value_info = 1;    // 筹码应该丢在那个位置
   repeated BetNumInfo bet_num_info = 2;        // 所有玩家在各个位置下注总量(龙虎和)
}

//开牌通知
message ShowCardNotify {
    repeated uint32 card_deck = 1;          //两张牌
    uint32 time_out = 2;                    //开牌的倒计时(单位秒)
}

//结算通知
message GameConcludeNotify  {
    uint32 result = 1;                          //胜利结果(1龙 2虎 3和)
    string gold = 2;                            //玩家结算后的金币数量
    string score = 3;                           //输赢的分数
    uint32 time_out = 4;                        //结束时间
    bool is_bet_req = 5;                        //是否有下注，true = 有下注,false = 没下注
}

//重连信息
message GameReconnectInfo  {
    RoomInfo room_info = 1;                        //房间信息
    uint32 game_state  = 2;                        //游戏状态 1=发牌 2=下注 3=开牌 4=结算
    uint32 time_out    = 3;                        //当前阶段剩下的时间(单位秒)
    repeated uint32 bet_value_arr = 4;             //该玩家能下倍数的数组
    repeated BetNumInfo bet_num_info = 5;          //龙虎斗已下注数量
    repeated BetValueInfo bet_value_info = 6;      //桌面筹码值 只保存最后 15 个下注筹码值
    repeated uint32 card_deck = 7;                 //到了开牌阶段 两张牌才有牌值
    repeated uint32 record_arr = 8;                //游戏历史记录 
    string gold = 9;                               //玩家剩余金币
    repeated uint32 limit_min_num = 10;            //龙虎斗最低限红数组
    repeated uint32 limit_max_num = 11;            //龙虎斗最大限红数组
    repeated uint32 game_multiple_arr = 12;        //龙虎斗倍数数组
}

//其它玩家列表
message OtherUsersReq {
    uint32 player_id = 1;
}

message OtherUsersRes {
    repeated PlayerInfo player_list = 1;      //20名的玩家信息
    uint32 totalplayers = 2;                  //房间总玩家数量
}
