syntax = "proto3";
package DHT;

message MsgBody  {

    GameStartNotify   game_start_notify = 1;             
    
    DiceListNotify    dice_list_notify = 2;         //玩家摇骰子消息

    StartRandomNotify   start_random_notify = 3;    //随机开始人

    StartOperateNotify  start_operate_notify = 4;   //开始操作

    DiceOperateReq      dice_operate_req = 5;       //请求操作内容

    DiceOperateNotify   dice_operate_notify = 6;       //回复操作结果

    PlayerInfoEnd       player_info_end = 7;        //牌局结束时的玩家列表
    GameResultNotify    game_result_notify = 8;     //游戏结果

    GameReconnectInfo game_reconnect_info = 9;
}

message PlayerInfo  {
    uint32 player_id = 1;
    uint32 seat_id = 2;      //座位ID
    string username = 3;
    float gold = 4;
    uint32 headimg_id = 5;
    uint32 sex = 6;          //性别
    uint32 vip_level = 7;   //VIP等级
}

message RoomInfo  {
    string room_base_note  = 1 ;      //房间底注
    string room_code = 2;             //房间编号信息
}

message GameStartNotify  {   //游戏开始通知
    repeated PlayerInfo player_list = 1;           //所有玩家
    // repeated uint32 grab_multiple_arr = 2;         //
    RoomInfo room_info = 2;
}

message DiceListNotify {     //玩家摇骰子消息
    uint32 player_id = 1;
    repeated uint32 dice_arr = 2;
}

message StartRandomNotify {  //随机开始人
    uint32 seat_id = 1;
}

message StartOperateNotify {  //开始操作
    uint32 seat_id = 1;
    uint32 wait_time = 2;
    uint32 operate_type = 3;  //1表示叫蛊2开3都可以
    uint32 min_num = 4;       //个数
    uint32 min_dice = 5;      //最小骰子点数
    uint32 turn = 6;          //轮次
}

message DiceOperateReq {      //操作内容
    uint32 player_id = 1;
    uint32 operate_type = 2;  //1表示叫蛊2开3都可以
    uint32 min_num = 3; 
    uint32 min_dice = 4;   
}

message DiceOperateNotify {      //操作结果
    uint32 seat_id = 1;
    uint32 operate_type = 2;
    uint32 min_num = 3;
    uint32 min_dice = 4;
}

message PlayerInfoEnd {                 //结束时玩家信息
    uint32 player_id = 1;
    uint32 player_type = 2;             //玩家类型,1表示庄家，0表示非庄家
    string player_name = 3;
    repeated uint32 dice_list = 4;      //骰子点数
    bool bunko = 5;                     //输赢
    string coin_num = 6;                //输赢金币数
    string gold = 7;                    //玩家当前金币
    uint32 vip_level = 8;               //玩家VIP等级
    uint32 headimg_id = 9;              //玩家头像ID
}

message GameResultNotify {    //游戏结果
    uint32 last_dice_value = 1;
    uint32 last_dice_num = 2;
    bool called_one = 3;      //true表示1被叫了
    uint32 num = 4;           //两副骰子包含被叫点的实际个数
    repeated PlayerInfoEnd player_info_end = 5;
}
message GameRecord { //游戏记录
    uint32 seat_id = 1;
    uint32 min_num = 2;
    uint32 min_dice = 3;
    uint32 operate_type = 4;
}

message GameReconnectInfo{
    GameStartNotify game_start_info = 1;
    StartOperateNotify game_operate_info = 2;
    repeated uint32 dice_arr = 3;    //该玩家的骰子数据
    repeated GameRecord game_record = 4;
    string gold = 5;                //玩家重连金币
}
