syntax = "proto3";
package BY;

message MsgBody {
  GameBulletCreateReq     game_bullet_create_req    = 1;    //子弹生成请求
  GameBulletCreateRes     game_bullet_create_res    = 2;    //子弹生成回应
  GameBulletDisappearReq  game_bullet_disappear_req = 3;    //子弹消失请求
  GameBulletDisappearRes  game_bullet_disappear_res = 4;    //子弹消失回应
  GameBulletAttackReq     game_bullet_attack_req    = 5;    //子弹攻击请求
  GameBulletChangeReq     game_bullet_change_req    = 6;    //改变子弹等级请求
  GameBulletChangeRes     game_bullet_change_res    = 7;    //改变子弹等级回应

  GameFishFreshNotify     game_fish_fresh_notify    = 8;    //鱼刷新通知
  GameFishDiedNotify      game_fish_died_notify     = 9;    //鱼死亡通知

  GameReconnectInfo       game_reconnect_info       = 10;

  GameOtherJoin           game_other_join           = 11;   //其他玩家加入
  GameOtherLeave          game_other_leave          = 12;   //其他玩家退出
  GameBulletAttackRes     game_bullet_attack_res    = 13;   //子弹攻击回应
  NewGameFishFreshNotify  new_game_reconnect_info   = 14; //新的鱼刷新协议(打包刷)
  PlayerDisConnect        player_dis_connect        = 15; //玩家掉线
  SendStatus              send_status               = 18; //有玩家进房组播所有人射击状态用来解决玩家进入射击状态
  FreshGold               fresh_gold                = 19; //刷新金币
}

message RoomInfo {
  string room_code = 1;             //房间编号信息
}

message PlayerInfo {
  uint32 player_id = 1;
  uint32 seat_id = 2;      //座位ID
  string username = 3;
  float gold = 4;
  uint32 headimg_id = 5;
  uint32 sex = 6;          //性别
  uint32 bullet_cur_level = 7;  //子弹等级 用于皮肤初始化
  uint32 angle = 8;                 //角度 正右侧为0度 逆时针角度增加
  uint32 vip_level = 9;
}

//游戏初始化请求
message GameInitReq {
  uint32 player_id = 1;
}

message BetInfo {
  uint32 bet_id = 1;      //下注ID
  uint32 bet_value = 2;   //下注值
}

//坐标
message Point {
  float x = 1;                      //x坐标
  float y = 2;                      //y坐标
}
message Bullet {
  string bullet_id = 1; //子弹ID
  Point point = 2; //坐标
  string angle = 3; //角度 正右侧为0度 逆时针角度增加
  uint32 speed = 4; //子弹速度
  uint32 bullet_level = 5; //子弹等级
}
//子弹生成请求
message GameBulletCreateReq {
  uint32 player_id = 1; //玩家id
  Bullet theBullet = 2; //客户端生成子弹告诉服务器数据
  uint32 lock_shot = 3; //锁定攻击 0 正常发射 1 自动攻击2 取消发射3
  uint32 fish_id = 4; //锁定鱼id 正常发射传0
  uint32 is_return = 5 ; //0不需要回应客户端 1需要回应客户端
}
//子弹生成回应
message GameBulletCreateRes {
  uint32 res = 1;                   //射击子弹结果 0成功 1下注无效 2金币不足 3屏幕中该玩家的子弹过多
  string bullet_id = 2;             //子弹id 成功时返回id 失败返回0
  Point point = 3;                  //坐标
  string gold = 4;                  //当前金币
  uint32 seat_id = 5;               //
  uint32 speed = 6;                 //子弹速度
  string angle = 7;                 //角度 正右侧为0度 逆时针角度增加
  uint32 bullet_level = 8;          //子弹等级
  uint32 lock_shot = 9;             //锁定攻击 0 正常发射 1 自动攻击2 取消发射3
  uint32 fish_id = 10;              //锁定鱼id
}

//子弹消失请求
message GameBulletDisappearReq {
  uint32 player_id = 1;             //玩家id
  repeated string bullet_id = 2; //子弹id
}

//子弹消失回应
message GameBulletDisappearRes {
  uint32 res = 1;                   //结果 0成功
  string gold = 2;                  //当前金币
  uint32 seat_id = 3;               //玩家ID
}

//子弹攻击请求
message GameBulletAttackReq {
  uint32 player_id = 1;             //玩家id
  string bullet_id = 2;             //子弹id
  uint32 fish_id = 3;               //鱼id
  bool is_my_bullet = 4;            //是否是自己的子弹
}

//改变子弹等级请求
message GameBulletChangeReq {
  uint32 player_id = 1;             //玩家id
  uint32 level = 2;                 //要切换到的目标子弹等级
}

//改变子弹等级回应
message GameBulletChangeRes {
  uint32 res = 1;                   //结果 0成功 1目标子弹等级错误
  uint32 player_id = 2;             //切换子弹的玩家
  uint32 seat_id = 3;               //切换子弹的玩家
  uint32 level = 4;                 //要切换到的目标子弹等级
}

message FishInfo {
  repeated uint32 fish_id = 1;      //鱼id
  uint32 fish_type = 2;             //鱼种类
}

//鱼刷新通知
message GameFishFreshNotify {
  repeated FishInfo fish_info = 1;  //鱼信息 刷新鱼潮和绑定鱼时会有多条
  uint32 count = 3;                 //刷新数量
  uint32 speed = 4;                 //鱼速度
  float time = 5;                   //运行时间
  uint32 line = 6;                  //鱼群路线
  uint32 form = 7;                  //鱼潮鱼群形状
  uint32 special = 8;               //0 普通鱼 1特殊鱼 2鱼潮
  uint32 times = 9;                 //鱼倍数 暂时为0
  float fish_size = 10;             //鱼大小
  uint32 level = 11;                //鱼的等级 正常情况下是1 进化后的鱼重连时等级不是1
}

message SkillInfo {
  uint32 id = 1;                    //技能id 0无 1全屏击杀 2冰冻 3狂暴收益提升 4进化 5转盘 6绑定鱼(大三元大四喜) 7一网打尽 8海盗船
  float time_out = 2;               //持续时间
  uint32 mul = 3;                   //倍数 只针对转盘有效
  uint32 level = 4;                 //鱼等级 只针对进化有效
  uint32 seat_id = 5;               //影响的座位id 只针对狂暴怪有效
}
message GameBulletAttackRes {
  uint32 player_id = 1; //玩家id
  string bullet_id = 2; //子弹id
  uint32 fish_id = 3; //鱼id
}

//鱼死亡通知
message GameFishDiedNotify {
  uint32 player_id = 1;             //击杀鱼的玩家
  repeated uint32 fish_id = 2;      //鱼id
  string score = 3;                 //获得的金币
  string gold = 4;                  //当前金币
  uint32 seat_id = 5;               //击杀鱼的玩家
  SkillInfo skill_info = 6;         //击杀鱼后的技能效果
  uint32 fish_times = 7 ;           //击杀鱼的倍数    
}

message GameReconnectInfo{
  RoomInfo room_info = 1;                     //房间信息
  PlayerInfo player_info = 2;                 //玩家信息
  repeated PlayerInfo other_player_info = 3;  //其他玩家信息
  repeated GameBulletCreateRes bullets = 4;   //子弹
  repeated GameFishFreshNotify fishs = 5;     //鱼
  repeated string bullet_level = 6;           //子弹等级数组
  repeated SkillInfo skill_info = 7;          //当前正在执行的技能效果
  repeated MonsterTimes monster_times = 8;    //怪物倍数列表
  repeated uint32 roll_times = 9;             //转盘倍数
}

message MonsterTimes {
  uint32 fish_type = 1;                      //鱼的类型
  float times = 2;                            //倍数
}

message GameOtherJoin {
  PlayerInfo player_info = 1; //当前玩家
}

message GameOtherLeave {
  PlayerInfo player_info = 1; //当前玩家
}

message NewGameFishFreshNotify {
  repeated GameFishFreshNotify fishs = 1; //鱼
}

message PlayerDisConnect{
  uint32 player_id = 1; //掉线玩家
}
message SendStatus {
  repeated ShotStatus status = 1;
}
message ShotStatus {
  uint32 player_id = 1; //玩家id
  uint32 status = 2; //锁定攻击 0 正常发射 1 自动攻击2 3无状态
  GameBulletCreateRes bullets = 3; //子弹
  uint32 seat_id = 4; //座位ID
}

message GoldInfo {
  uint32 seat_id = 1; //座位ID
  string gold = 2; //金币

}
message FreshGold {
  repeated GoldInfo gold_info = 1;
}
